import type { Metadata } from 'next';

export const metadata: Metadata = {
  title: 'About Us | On The Way',
  description: "Learn about On The Way's mission, values, and team",
};

export const dynamic = 'force-dynamic';

export default function AboutPage() {
  return (
    <div className="container mx-auto py-10">
      <div className="flex flex-col space-y-12">
        {/* Hero Section */}
        <div className="text-center space-y-4">
          <h1 className="text-4xl font-bold tracking-tight md:text-5xl">
            About On The Way
          </h1>
          <p className="text-xl text-muted-foreground max-w-3xl mx-auto">
            We're on a mission to make delivery services more accessible,
            reliable, and community-focused.
          </p>
        </div>

        {/* Mission Section */}
        <div className="grid grid-cols-1 md:grid-cols-2 gap-8 items-center">
          <div className="space-y-4">
            <h2 className="text-3xl font-bold">Our Mission</h2>
            <p className="text-lg text-muted-foreground">
              At On The Way, we believe everyone deserves access to reliable
              delivery services, regardless of where they live. We&apos;re a
              team of passionate individuals dedicated to bringing you the best
              products and services. Our mission is to provide a seamless and
              enjoyable experience for all our customers, ensuring you find
              exactly what you&apos;re looking for.
            </p>
            <p className="text-lg text-muted-foreground">
              {/* Terms Page Link */}
            </p>
          </div>
        </div>
      </div>
    </div>
  );
}

'use client';

import { useState, useEffect } from 'react';
import { useAuth } from '../../contexts/AuthContext';
import { useRouter } from 'next/navigation';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '../../components/ui/card';
import { Button } from '../../components/ui/button';
import { Badge } from '../../components/ui/badge';
import { Input } from '../../components/ui/input';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '../../components/ui/select';
import { Clock, MapPin, User, Package, DollarSign, Search, Filter, RefreshCw } from 'lucide-react';
import Link from 'next/link';

interface Order {
  id: string;
  orderId: string;
  customerName: string;
  customerPhone: string;
  customerEmail: string;
  orderType: 'food' | 'grocery' | 'package' | 'ride';
  status: 'pending' | 'confirmed' | 'preparing' | 'out_for_delivery' | 'delivered' | 'cancelled';
  items: string[];
  total: number;
  address: string;
  estimatedDeliveryTime: string;
  createdAt: string;
  assignedDriver?: string;
  specialInstructions?: string;
}

const ADMIN_EMAILS = [
  'admin@otw.com',
  'manager@otw.com',
  'supervisor@otw.com',
];

export default function AdminDashboard() {
  const { user, isLoading } = useAuth();
  const router = useRouter();
  const [orders, setOrders] = useState<Order[]>([]);
  const [filteredOrders, setFilteredOrders] = useState<Order[]>([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [statusFilter, setStatusFilter] = useState('all');
  const [typeFilter, setTypeFilter] = useState('all');
  const [isLoadingOrders, setIsLoadingOrders] = useState(true);

  // Check if user is admin
  const isAdmin = user && ADMIN_EMAILS.includes(user.email || '');

  useEffect(() => {
    if (!isLoading && !user) {
      router.push('/signin?redirect=/admin');
      return;
    }

    if (!isLoading && user && !isAdmin) {
      router.push('/');
      return;
    }

    if (isAdmin) {
      fetchOrders();
    }
  }, [user, isLoading, isAdmin, router]);

  useEffect(() => {
    filterOrders();
  }, [orders, searchTerm, statusFilter, typeFilter]);

  const fetchOrders = async () => {
    try {
      setIsLoadingOrders(true);
      // TODO: Replace with actual API call to fetch orders from backend
      // const response = await fetch('/api/orders');
      // const orders = await response.json();
      // setOrders(orders);

      // For now, set empty array until backend is implemented
      setOrders([]);
    } catch (error) {
      console.error('Error fetching orders:', error);
    } finally {
      setIsLoadingOrders(false);
    }
  };

  const filterOrders = () => {
    let filtered = orders.filter(order => order.status !== 'delivered' && order.status !== 'cancelled');

    if (searchTerm) {
      filtered = filtered.filter(order =>
        order.orderId.toLowerCase().includes(searchTerm.toLowerCase()) ||
        order.customerName.toLowerCase().includes(searchTerm.toLowerCase()) ||
        order.customerEmail.toLowerCase().includes(searchTerm.toLowerCase()),
      );
    }

    if (statusFilter !== 'all') {
      filtered = filtered.filter(order => order.status === statusFilter);
    }

    if (typeFilter !== 'all') {
      filtered = filtered.filter(order => order.orderType === typeFilter);
    }

    setFilteredOrders(filtered);
  };

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'pending': return 'bg-yellow-500/20 text-yellow-400';
      case 'confirmed': return 'bg-blue-500/20 text-blue-400';
      case 'preparing': return 'bg-orange-500/20 text-orange-400';
      case 'out_for_delivery': return 'bg-green-500/20 text-green-400';
      default: return 'bg-gray-500/20 text-gray-400';
    }
  };

  const getTypeIcon = (type: string) => {
    switch (type) {
      case 'food': return 'ðŸ•';
      case 'grocery': return 'ðŸ›’';
      case 'package': return 'ðŸ“¦';
      case 'ride': return 'ðŸš—';
      default: return 'ðŸ“‹';
    }
  };

  const updateOrderStatus = async (orderId: string, newStatus: string) => {
    try {
      // Mock update - replace with actual API call
      setOrders(prev => prev.map(order =>
        order.id === orderId ? { ...order, status: newStatus as any } : order,
      ));
    } catch (error) {
      console.error('Error updating order status:', error);
    }
  };

  if (isLoading) {
    return (
      <div className="min-h-screen bg-black flex items-center justify-center">
        <div className="text-white text-xl">Loading...</div>
      </div>
    );
  }

  if (!user) {
    return null; // Will redirect to signin
  }

  if (!isAdmin) {
    return (
      <div className="min-h-screen bg-black flex items-center justify-center">
        <div className="text-center">
          <h1 className="text-2xl font-bold text-red-500 mb-4">Access Denied</h1>
          <p className="text-white mb-4">You don't have permission to access this page.</p>
          <Link href="/" className="text-otw-gold hover:underline">Go Home</Link>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-black text-white p-6">
      <div className="max-w-7xl mx-auto">
        {/* Header */}
        <div className="mb-8">
          <h1 className="text-4xl font-bold text-otw-gold mb-2">Admin Dashboard</h1>
          <p className="text-gray-300">Manage all active orders and deliveries</p>
        </div>

        {/* Stats Cards */}
        <div className="grid grid-cols-1 md:grid-cols-4 gap-6 mb-8">
          <Card className="bg-gray-900 border-gray-800">
            <CardHeader className="pb-2">
              <CardTitle className="text-sm font-medium text-gray-400">Total Active Orders</CardTitle>
            </CardHeader>
            <CardContent>
              <div className="text-2xl font-bold text-white">{filteredOrders.length}</div>
            </CardContent>
          </Card>
          <Card className="bg-gray-900 border-gray-800">
            <CardHeader className="pb-2">
              <CardTitle className="text-sm font-medium text-gray-400">Pending Orders</CardTitle>
            </CardHeader>
            <CardContent>
              <div className="text-2xl font-bold text-yellow-400">
                {filteredOrders.filter(o => o.status === 'pending').length}
              </div>
            </CardContent>
          </Card>
          <Card className="bg-gray-900 border-gray-800">
            <CardHeader className="pb-2">
              <CardTitle className="text-sm font-medium text-gray-400">Out for Delivery</CardTitle>
            </CardHeader>
            <CardContent>
              <div className="text-2xl font-bold text-green-400">
                {filteredOrders.filter(o => o.status === 'out_for_delivery').length}
              </div>
            </CardContent>
          </Card>
          <Card className="bg-gray-900 border-gray-800">
            <CardHeader className="pb-2">
              <CardTitle className="text-sm font-medium text-gray-400">Total Revenue</CardTitle>
            </CardHeader>
            <CardContent>
              <div className="text-2xl font-bold text-otw-gold">
                ${filteredOrders.reduce((sum, order) => sum + order.total, 0).toFixed(2)}
              </div>
            </CardContent>
          </Card>
        </div>

        {/* Filters */}
        <Card className="bg-gray-900 border-gray-800 mb-6">
          <CardHeader>
            <CardTitle className="flex items-center gap-2">
              <Filter className="w-5 h-5" />
              Filters
            </CardTitle>
          </CardHeader>
          <CardContent>
            <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
              <div className="relative">
                <Search className="absolute left-3 top-3 w-4 h-4 text-gray-400" />
                <Input
                  placeholder="Search orders..."
                  value={searchTerm}
                  onChange={(e) => setSearchTerm(e.target.value)}
                  className="pl-10 bg-gray-800 border-gray-700"
                />
              </div>
              <Select value={statusFilter} onValueChange={setStatusFilter}>
                <SelectTrigger className="bg-gray-800 border-gray-700">
                  <SelectValue placeholder="Filter by status" />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="all">All Statuses</SelectItem>
                  <SelectItem value="pending">Pending</SelectItem>
                  <SelectItem value="confirmed">Confirmed</SelectItem>
                  <SelectItem value="preparing">Preparing</SelectItem>
                  <SelectItem value="out_for_delivery">Out for Delivery</SelectItem>
                </SelectContent>
              </Select>
              <Select value={typeFilter} onValueChange={setTypeFilter}>
                <SelectTrigger className="bg-gray-800 border-gray-700">
                  <SelectValue placeholder="Filter by type" />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="all">All Types</SelectItem>
                  <SelectItem value="food">Food Delivery</SelectItem>
                  <SelectItem value="grocery">Grocery</SelectItem>
                  <SelectItem value="package">Package</SelectItem>
                  <SelectItem value="ride">Ride</SelectItem>
                </SelectContent>
              </Select>
              <Button onClick={fetchOrders} variant="outline" className="border-gray-700">
                <RefreshCw className="w-4 h-4 mr-2" />
                Refresh
              </Button>
            </div>
          </CardContent>
        </Card>

        {/* Orders List */}
        <div className="space-y-4">
          {isLoadingOrders ? (
            <div className="text-center py-8">
              <div className="text-gray-400">Loading orders...</div>
            </div>
          ) : filteredOrders.length === 0 ? (
            <Card className="bg-gray-900 border-gray-800">
              <CardContent className="text-center py-8">
                <div className="text-gray-400">No active orders found</div>
              </CardContent>
            </Card>
          ) : (
            filteredOrders.map((order) => (
              <Card key={order.id} className="bg-gray-900 border-gray-800">
                <CardHeader>
                  <div className="flex justify-between items-start">
                    <div>
                      <CardTitle className="flex items-center gap-2">
                        <span className="text-2xl">{getTypeIcon(order.orderType)}</span>
                        {order.orderId}
                      </CardTitle>
                      <CardDescription className="text-gray-400">
                        {new Date(order.createdAt).toLocaleString()}
                      </CardDescription>
                    </div>
                    <Badge className={getStatusColor(order.status)}>
                      {order.status.replace('_', ' ').toUpperCase()}
                    </Badge>
                  </div>
                </CardHeader>
                <CardContent>
                  <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
                    <div>
                      <h4 className="font-semibold text-white mb-2 flex items-center gap-2">
                        <User className="w-4 h-4" />
                        Customer
                      </h4>
                      <p className="text-gray-300">{order.customerName}</p>
                      <p className="text-gray-400 text-sm">{order.customerPhone}</p>
                      <p className="text-gray-400 text-sm">{order.customerEmail}</p>
                    </div>
                    <div>
                      <h4 className="font-semibold text-white mb-2 flex items-center gap-2">
                        <MapPin className="w-4 h-4" />
                        Delivery Address
                      </h4>
                      <p className="text-gray-300">{order.address}</p>
                      <p className="text-gray-400 text-sm flex items-center gap-1">
                        <Clock className="w-3 h-3" />
                        ETA: {order.estimatedDeliveryTime}
                      </p>
                    </div>
                    <div>
                      <h4 className="font-semibold text-white mb-2 flex items-center gap-2">
                        <Package className="w-4 h-4" />
                        Order Details
                      </h4>
                      <div className="text-gray-300 text-sm space-y-1">
                        {order.items.map((item, index) => (
                          <div key={index}>{item}</div>
                        ))}
                      </div>
                      <p className="text-otw-gold font-semibold mt-2 flex items-center gap-1">
                        <DollarSign className="w-4 h-4" />
                        ${order.total.toFixed(2)}
                      </p>
                    </div>
                  </div>

                  {order.assignedDriver && (
                    <div className="mt-4 p-3 bg-gray-800 rounded-lg">
                      <p className="text-gray-300">
                        <strong>Assigned Driver:</strong> {order.assignedDriver}
                      </p>
                    </div>
                  )}

                  {order.specialInstructions && (
                    <div className="mt-4 p-3 bg-gray-800 rounded-lg">
                      <p className="text-gray-300">
                        <strong>Special Instructions:</strong> {order.specialInstructions}
                      </p>
                    </div>
                  )}

                  <div className="mt-4 flex gap-2">
                    {order.status === 'pending' && (
                      <Button
                        onClick={() => updateOrderStatus(order.id, 'confirmed')}
                        className="bg-blue-600 hover:bg-blue-700"
                      >
                        Confirm Order
                      </Button>
                    )}
                    {order.status === 'confirmed' && (
                      <Button
                        onClick={() => updateOrderStatus(order.id, 'preparing')}
                        className="bg-orange-600 hover:bg-orange-700"
                      >
                        Start Preparing
                      </Button>
                    )}
                    {order.status === 'preparing' && (
                      <Button
                        onClick={() => updateOrderStatus(order.id, 'out_for_delivery')}
                        className="bg-green-600 hover:bg-green-700"
                      >
                        Out for Delivery
                      </Button>
                    )}
                    {order.status === 'out_for_delivery' && (
                      <Button
                        onClick={() => updateOrderStatus(order.id, 'delivered')}
                        className="bg-otw-gold hover:bg-yellow-600 text-black"
                      >
                        Mark as Delivered
                      </Button>
                    )}
                  </div>
                </CardContent>
              </Card>
            ))
          )}
        </div>
      </div>
    </div>
  );
}

'use client';

export const dynamic = 'force-dynamic';

import { useState, useEffect } from 'react';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '../../../components/ui/card';
import { Button } from '../../../components/ui/button';
import { Badge } from '../../../components/ui/badge';
import { Input } from '../../../components/ui/input';
import { Textarea } from '../../../components/ui/textarea';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '../../../components/ui/select';
import { AlertDialog, AlertDialogAction, AlertDialogCancel, AlertDialogContent, AlertDialogDescription, AlertDialogFooter, AlertDialogHeader, AlertDialogTitle, AlertDialogTrigger } from '../../../components/ui/alert-dialog';
import { Label } from '../../../components/ui/label';
import { Camera, Phone, Mail, MapPin, DollarSign, Clock, User, FileText, RefreshCw } from 'lucide-react';
import Image from 'next/image';

interface ScreenshotOrder {
  id: string;
  orderId: string;
  status: string;
  customerInfo: {
    name: string;
    phone: string;
    email: string;
  };
  restaurantInfo: {
    name: string;
    pickupLocation: string;
  };
  orderDetails: {
    estimatedTotal: string;
    specialInstructions: string;
    screenshotUrl: string;
    originalFileName: string;
  };
  timestamps: {
    created: string;
    updated: string;
  };
  workflow: {
    reviewRequired: boolean;
    confirmationCalled: boolean;
    orderPlaced: boolean;
    pickedUp: boolean;
    delivered: boolean;
  };
  adminNotes?: string;
  lastUpdatedBy?: string;
}

const statusColors = {
  pending_review: 'bg-yellow-100 text-yellow-800',
  confirmed: 'bg-blue-100 text-blue-800',
  order_placed: 'bg-purple-100 text-purple-800',
  picked_up: 'bg-orange-100 text-orange-800',
  out_for_delivery: 'bg-indigo-100 text-indigo-800',
  delivered: 'bg-green-100 text-green-800',
  cancelled: 'bg-red-100 text-red-800',
  failed: 'bg-gray-100 text-gray-800',
};

const statusOptions = [
  { value: 'pending_review', label: 'Pending Review' },
  { value: 'confirmed', label: 'Confirmed' },
  { value: 'order_placed', label: 'Order Placed' },
  { value: 'picked_up', label: 'Picked Up' },
  { value: 'out_for_delivery', label: 'Out for Delivery' },
  { value: 'delivered', label: 'Delivered' },
  { value: 'cancelled', label: 'Cancelled' },
  { value: 'failed', label: 'Failed' },
];

export default function ScreenshotOrdersAdmin() {
  const [orders, setOrders] = useState<ScreenshotOrder[]>([]);
  const [loading, setLoading] = useState(true);
  const [selectedOrder, setSelectedOrder] = useState<ScreenshotOrder | null>(null);
  const [statusFilter, setStatusFilter] = useState<string>('all');
  const [searchTerm, setSearchTerm] = useState('');
  const [updateStatus, setUpdateStatus] = useState('');
  const [updateNotes, setUpdateNotes] = useState('');
  const [updating, setUpdating] = useState(false);

  const fetchOrders = async () => {
    try {
      setLoading(true);
      const params = new URLSearchParams();
      if (statusFilter !== 'all') {
        params.append('status', statusFilter);
      }
      params.append('limit', '100');

      const response = await fetch(`/api/orders/screenshot?${params}`);
      const data = await response.json();

      if (data.orders) {
        setOrders(data.orders);
      }
    } catch (error) {
      console.error('Error fetching orders:', error);
    } finally {
      setLoading(false);
    }
  };

  const updateOrderStatus = async () => {
    if (!selectedOrder || !updateStatus) {return;}

    try {
      setUpdating(true);
      const response = await fetch('/api/orders/screenshot', {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          orderId: selectedOrder.id,
          status: updateStatus,
          notes: updateNotes,
          adminId: 'admin', // In a real app, this would be the logged-in admin's ID
        }),
      });

      if (response.ok) {
        // Refresh orders
        await fetchOrders();
        // Reset form
        setUpdateStatus('');
        setUpdateNotes('');
        setSelectedOrder(null);
      }
    } catch (error) {
      console.error('Error updating order:', error);
    } finally {
      setUpdating(false);
    }
  };

  useEffect(() => {
    fetchOrders();
  }, [statusFilter]);

  const filteredOrders = orders.filter(order => {
    if (searchTerm) {
      const searchLower = searchTerm.toLowerCase();
      return (
        order.orderId.toLowerCase().includes(searchLower) ||
        order.customerInfo.name.toLowerCase().includes(searchLower) ||
        order.customerInfo.phone.includes(searchTerm) ||
        order.restaurantInfo.name.toLowerCase().includes(searchLower)
      );
    }
    return true;
  });

  const formatDate = (dateString: string) => {
    return new Date(dateString).toLocaleString();
  };

  return (
    <div className="min-h-screen bg-gray-50 py-8">
      <div className="container mx-auto px-4 max-w-7xl">
        {/* Header */}
        <div className="flex justify-between items-center mb-8">
          <div>
            <h1 className="text-3xl font-bold text-gray-900">Screenshot Orders</h1>
            <p className="text-gray-600 mt-2">Manage customer screenshot orders</p>
          </div>
          <Button onClick={fetchOrders} disabled={loading}>
            <RefreshCw className={`h-4 w-4 mr-2 ${loading ? 'animate-spin' : ''}`} />
            Refresh
          </Button>
        </div>

        {/* Filters */}
        <Card className="mb-6">
          <CardContent className="pt-6">
            <div className="flex flex-col sm:flex-row gap-4">
              <div className="flex-1">
                <Label htmlFor="search">Search Orders</Label>
                <Input
                  id="search"
                  placeholder="Search by order ID, customer name, phone, or restaurant..."
                  value={searchTerm}
                  onChange={(e) => setSearchTerm(e.target.value)}
                />
              </div>
              <div className="w-full sm:w-48">
                <Label htmlFor="status-filter">Filter by Status</Label>
                <Select value={statusFilter} onValueChange={setStatusFilter}>
                  <SelectTrigger>
                    <SelectValue placeholder="All statuses" />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="all">All Statuses</SelectItem>
                    {statusOptions.map((option) => (
                      <SelectItem key={option.value} value={option.value}>
                        {option.label}
                      </SelectItem>
                    ))}
                  </SelectContent>
                </Select>
              </div>
            </div>
          </CardContent>
        </Card>

        {/* Orders List */}
        {loading ? (
          <div className="flex items-center justify-center py-12">
            <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-orange-500"></div>
          </div>
        ) : (
          <div className="grid gap-6">
            {filteredOrders.length === 0 ? (
              <Card>
                <CardContent className="pt-6 text-center py-12">
                  <Camera className="h-12 w-12 text-gray-400 mx-auto mb-4" />
                  <p className="text-gray-500">No orders found</p>
                </CardContent>
              </Card>
            ) : (
              filteredOrders.map((order) => (
                <Card key={order.id} className="hover:shadow-md transition-shadow">
                  <CardContent className="pt-6">
                    <div className="flex flex-col lg:flex-row gap-6">
                      {/* Order Info */}
                      <div className="flex-1 space-y-4">
                        <div className="flex items-center justify-between">
                          <div>
                            <h3 className="text-lg font-semibold">{order.orderId}</h3>
                            <p className="text-sm text-gray-500">
                              Created: {formatDate(order.timestamps.created)}
                            </p>
                          </div>
                          <Badge className={statusColors[order.status as keyof typeof statusColors]}>
                            {statusOptions.find(s => s.value === order.status)?.label || order.status}
                          </Badge>
                        </div>

                        <div className="grid md:grid-cols-2 gap-4">
                          <div className="space-y-2">
                            <div className="flex items-center gap-2">
                              <User className="h-4 w-4 text-gray-400" />
                              <span className="font-medium">{order.customerInfo.name}</span>
                            </div>
                            <div className="flex items-center gap-2">
                              <Phone className="h-4 w-4 text-gray-400" />
                              <span className="text-sm">{order.customerInfo.phone}</span>
                            </div>
                            <div className="flex items-center gap-2">
                              <Mail className="h-4 w-4 text-gray-400" />
                              <span className="text-sm">{order.customerInfo.email}</span>
                            </div>
                          </div>
                          <div className="space-y-2">
                            <div className="flex items-center gap-2">
                              <MapPin className="h-4 w-4 text-gray-400" />
                              <span className="font-medium">{order.restaurantInfo.name}</span>
                            </div>
                            <div className="flex items-center gap-2">
                              <MapPin className="h-4 w-4 text-gray-400" />
                              <span className="text-sm">{order.restaurantInfo.pickupLocation}</span>
                            </div>
                            <div className="flex items-center gap-2">
                              <DollarSign className="h-4 w-4 text-gray-400" />
                              <span className="text-sm font-medium">{order.orderDetails.estimatedTotal}</span>
                            </div>
                          </div>
                        </div>

                        {order.orderDetails.specialInstructions && (
                          <div className="bg-gray-50 p-3 rounded-lg">
                            <div className="flex items-center gap-2 mb-1">
                              <FileText className="h-4 w-4 text-gray-400" />
                              <span className="text-sm font-medium">Special Instructions:</span>
                            </div>
