import { NextRequest, NextResponse } from 'next/server';
// import { firestore } from "../../../../../lib/firebaseAdmin";
// import { auth } from "../../../../../lib/firebaseAdmin";
import { menuItemSchema } from '../../../../../lib/firestoreModels';
import { handleAPIError, apiErrors } from '../../../../../lib/utils/apiErrors';
import { z } from 'zod';
import {
  AuthUser,
  BulkOperationResult,
  VerifiedItem,
} from '../../../../../lib/types/firebase';

export const dynamic = 'force-dynamic';

// Bulk operation schema
const bulkOperationSchema = z.object({
  operation: z.enum(['update', 'delete']),
  items: z
    .array(
      z.object({
        id: z.string().min(1),
        data: z.object({}).optional(), // For updates only
      }),
    )
    .min(1)
    .max(1000),
});

// async function isAdmin(userId: string) {
//   const userSnap = await firestore.collection("users").doc(userId).get();
//   const userData = userSnap.data();
//   return userSnap.exists && userData ? userData.role === "admin" : false;
// }

export async function POST(req: NextRequest) {
  try {
    // Temporarily disabled admin functionality
    return NextResponse.json({
      error: 'Admin functionality is currently being set up. Please check back soon!',
    }, { status: 503 });

    // const authHeader = req.headers.get("authorization");
    // if (!authHeader?.startsWith("Bearer ")) {
    //   throw apiErrors.unauthorized();
    // }

    // const idToken = authHeader.split(" ")[1];
    // if (!idToken) {
    //   throw apiErrors.unauthorized("Invalid authentication token");
    // }
    // const decoded = (await auth.verifyIdToken(idToken)) as AuthUser;
    // const userId = decoded.uid;

    // if (!(await isAdmin(userId))) {
    //   throw apiErrors.forbidden("Only admins can perform bulk operations");
    // }

    const data = await req.json();
    const { operation, items } = bulkOperationSchema.parse(data);

    // Validate update data if operation is update
    if (operation === 'update') {
      const invalidItems = items.filter((item) => !item.data);
      if (invalidItems.length > 0) {
        throw apiErrors.badRequest(
          'Data field is required for update operations',
          { invalidItems: invalidItems.map((item) => item.id) },
        );
      }
    }

    const results: BulkOperationResult = {
      success: [],
      failed: [],
    };

    // Process items in batches of 500 (Firestore limit)
    for (let i = 0; i < items.length; i += 500) {
      const batch = firestore.batch();
      const batchItems = items.slice(i, i + 500);

      // First, verify all documents exist and validate update data
      const verificationPromises = batchItems.map(async (item) => {
        try {
          const docRef = firestore.collection('menuItems').doc(item.id);
          const docSnap = await docRef.get();

          if (!docSnap.exists) {
            throw apiErrors.notFound(`Menu item ${item.id} not found`);
          }

          if (operation === 'update' && item.data) {
            // Validate update data
            const validatedData = menuItemSchema.partial().parse(item.data);
            return {
              docRef,
              docSnap,
              validatedData,
              id: item.id,
            };
          }

          return {
            docRef,
            docSnap,
            id: item.id,
          };
        } catch {
          results.failed.push({
            id: item.id,
            error: 'Unknown error',
          });
          return null;
        }
      });

      const verifiedItems = (await Promise.all(verificationPromises)).filter(
        Boolean,
      ) as VerifiedItem[];

      // Apply batch operations for verified items
      for (const item of verifiedItems) {
        if (!item) {continue;}

        try {
          if (operation === 'delete') {
            batch.delete(item.docRef);
          } else if (operation === 'update' && 'validatedData' in item) {
            batch.update(item.docRef, {
              ...item.validatedData,
              updatedAt: new Date(),
              // updatedBy: userId,
            });
          }
          if ('validatedData' in item) {
            const successItem: any = { id: item.id };
            if (item.validatedData) {
              successItem.data = item.validatedData;
            }
            results.success.push(successItem);
          } else {
            results.success.push({
              id: item.id,
            });
          }
        } catch {
          results.failed.push({
            id: item.id,
            error: 'Unknown error',
          });
        }
      }

      // Commit the batch if there are any operations
      if (verifiedItems.length > 0) {
        try {
          await batch.commit();
        } catch {
          // Move all items in this batch to failed
          verifiedItems.forEach((item: VerifiedItem) => {
            if (!item) {return;}
            results.success = results.success.filter((s) => s.id !== item.id);
            results.failed.push({
              id: item.id,
              error: 'Batch commit failed',
            });
          });
        }
      }
    }

    // If all operations failed, return an error
    if (results.success.length === 0 && results.failed.length > 0) {
      throw apiErrors.badRequest('All operations failed', {
        failed: results.failed,
      });
    }

    return NextResponse.json({
      message: `Bulk ${operation} completed`,
      results: {
        successCount: results.success.length,
        failureCount: results.failed.length,
        successful: results.success,
        failed: results.failed,
      },
    });
  } catch (err: unknown) {
    return handleAPIError(err);
  }
}

import { NextRequest, NextResponse } from 'next/server';
import { db } from '../../../../lib/firebase';
import { collection, doc, getDocs, getDoc, addDoc, updateDoc, deleteDoc, query, orderBy } from 'firebase/firestore';
import { getAuth } from 'firebase-admin/auth';

export const dynamic = 'force-dynamic';

interface MenuItem {
  id?: string;
  name: string;
  description: string;
  price: number;
  category: string;
  image?: string;
  available: boolean;
  ingredients?: string[];
  allergens?: string[];
  nutritionalInfo?: {
    calories?: number;
    protein?: number;
    carbs?: number;
    fat?: number;
  };
  preparationTime?: number;
  spicyLevel?: number;
  isVegetarian?: boolean;
  isVegan?: boolean;
  isGlutenFree?: boolean;
  createdAt?: string;
  updatedAt?: string;
}

// Verify admin access
async function verifyAdminAccess(request: NextRequest) {
  const authHeader = request.headers.get('authorization');
  if (!authHeader || !authHeader.startsWith('Bearer ')) {
    throw new Error('Authentication required');
  }

  const token = authHeader.split(' ')[1];

  try {
    const { getAuth } = await import('firebase-admin/auth');
    const decodedToken = await getAuth().verifyIdToken(token);

    // Check if user has admin role
    const userRef = doc(db, 'users', decodedToken.uid);
    const userDoc = await getDoc(userRef);

    if (!userDoc.exists()) {
      throw new Error('User not found');
    }

    const userData = userDoc.data();
    if (userData.role !== 'admin' && userData.role !== 'super_admin') {
      throw new Error('Admin access required');
    }

    return decodedToken.uid;
  } catch (error) {
    throw new Error('Invalid authentication token');
  }
}

// GET - Retrieve all menu items
export async function GET(req: NextRequest) {
  try {
    await verifyAdminAccess(req);

    const menuCollection = collection(db, 'menu_items');
    const q = query(menuCollection, orderBy('category'), orderBy('name'));
    const querySnapshot = await getDocs(q);

    const menuItems: MenuItem[] = [];
    querySnapshot.forEach((doc) => {
      menuItems.push({
        id: doc.id,
        ...doc.data(),
      } as MenuItem);
    });

    return NextResponse.json({
      success: true,
      data: menuItems,
      count: menuItems.length,
    });

  } catch (error) {
    console.error('Admin menu GET error:', error);

    if (error instanceof Error) {
      if (error.message.includes('Authentication') || error.message.includes('Admin access')) {
        return NextResponse.json(
          { error: error.message },
          { status: 401 },
        );
      }
    }

    return NextResponse.json(
      { error: 'Internal server error' },
      { status: 500 },
    );
  }
}

// POST - Create new menu item
export async function POST(req: NextRequest) {
  try {
    await verifyAdminAccess(req);

    const menuItem: MenuItem = await req.json();

    // Validate required fields
    if (!menuItem.name || !menuItem.description || !menuItem.price || !menuItem.category) {
      return NextResponse.json(
        { error: 'Missing required fields: name, description, price, category' },
        { status: 400 },
      );
    }

    // Add timestamps
    const now = new Date().toISOString();
    const newMenuItem = {
      ...menuItem,
      available: menuItem.available ?? true,
      createdAt: now,
      updatedAt: now,
    };

    const docRef = await addDoc(collection(db, 'menu_items'), newMenuItem);

    return NextResponse.json({
      success: true,
      data: {
        id: docRef.id,
        ...newMenuItem,
      },
      message: 'Menu item created successfully',
    });

  } catch (error) {
    console.error('Admin menu POST error:', error);

    if (error instanceof Error) {
      if (error.message.includes('Authentication') || error.message.includes('Admin access')) {
        return NextResponse.json(
          { error: error.message },
          { status: 401 },
        );
      }
    }

    return NextResponse.json(
      { error: 'Internal server error' },
      { status: 500 },
    );
  }
}

// PUT - Update existing menu item
export async function PUT(req: NextRequest) {
  try {
    await verifyAdminAccess(req);

    const { searchParams } = new URL(req.url);
    const itemId = searchParams.get('id');

    if (!itemId) {
      return NextResponse.json(
        { error: 'Menu item ID required' },
        { status: 400 },
      );
    }

    const updates: Partial<MenuItem> = await req.json();

    // Check if item exists
    const itemRef = doc(db, 'menu_items', itemId);
    const itemDoc = await getDoc(itemRef);

    if (!itemDoc.exists()) {
      return NextResponse.json(
        { error: 'Menu item not found' },
        { status: 404 },
      );
    }

    // Add update timestamp
    const updatedData = {
      ...updates,
      updatedAt: new Date().toISOString(),
    };

    await updateDoc(itemRef, updatedData);

    // Get updated document
    const updatedDoc = await getDoc(itemRef);

    return NextResponse.json({
      success: true,
      data: {
        id: updatedDoc.id,
        ...updatedDoc.data(),
      },
      message: 'Menu item updated successfully',
    });

  } catch (error) {
    console.error('Admin menu PUT error:', error);

    if (error instanceof Error) {
      if (error.message.includes('Authentication') || error.message.includes('Admin access')) {
        return NextResponse.json(
          { error: error.message },
          { status: 401 },
        );
      }
    }

    return NextResponse.json(
      { error: 'Internal server error' },
      { status: 500 },
    );
  }
}

// DELETE - Remove menu item
export async function DELETE(req: NextRequest) {
  try {
    await verifyAdminAccess(req);

    const { searchParams } = new URL(req.url);
    const itemId = searchParams.get('id');

    if (!itemId) {
      return NextResponse.json(
        { error: 'Menu item ID required' },
        { status: 400 },
      );
    }

    // Check if item exists
    const itemRef = doc(db, 'menu_items', itemId);
    const itemDoc = await getDoc(itemRef);

    if (!itemDoc.exists()) {
      return NextResponse.json(
        { error: 'Menu item not found' },
        { status: 404 },
      );
    }

    await deleteDoc(itemRef);

    return NextResponse.json({
      success: true,
      message: 'Menu item deleted successfully',
    });

  } catch (error) {
    console.error('Admin menu DELETE error:', error);

    if (error.message.includes('Authentication') || error.message.includes('Admin access')) {
      return NextResponse.json(
        { error: error.message },
        { status: 401 },
      );
    }

    return NextResponse.json(
      { error: 'Internal server error' },
      { status: 500 },
    );
  }
}

