// Helper function to sync all restaurants
async function syncAllRestaurants(force = false): Promise<SyncResult[]> {
  const results: SyncResult[] = [];

  try {
    // Get all enabled sync configs
    const configQuery = query(
      collection(db, 'sync_configs'),
      where('enabled', '==', true),
    );

    const configSnapshot = await getDocs(configQuery);

    for (const configDoc of configSnapshot.docs) {
      const config = configDoc.data() as SyncConfig;

      try {
        const result = await syncRestaurantMenu(config.restaurantId, config.source, force);
        results.push(result);
      } catch (error) {
        results.push({
          restaurantId: config.restaurantId,
          source: config.source,
          status: 'error',
          itemsAdded: 0,
          itemsUpdated: 0,
          itemsRemoved: 0,
          error: error instanceof Error ? error.message : 'Unknown error',
          timestamp: new Date().toISOString(),
        });
      }
    }

  } catch (error) {
    console.error('Sync all restaurants error:', error);
  }

  return results;
}

// Helper function to sync individual restaurant menu
async function syncRestaurantMenu(restaurantId: string, source?: string, force = false): Promise<SyncResult> {
  const timestamp = new Date().toISOString();

  try {
    // Get sync config
    const configQuery = query(
      collection(db, 'sync_configs'),
      where('restaurantId', '==', restaurantId),
      where('enabled', '==', true),
    );

    const configSnapshot = await getDocs(configQuery);

    if (configSnapshot.empty) {
      return {
        restaurantId,
        source: source || 'unknown',
        status: 'skipped',
        itemsAdded: 0,
        itemsUpdated: 0,
        itemsRemoved: 0,
        error: 'No enabled sync config found',
        timestamp,
      };
    }

    const config = configSnapshot.docs[0].data() as SyncConfig;
    const actualSource = source || config.source;

    // Check if sync is needed (unless forced)
    if (!force && config.lastSync) {
      const lastSyncTime = new Date(config.lastSync);
      const now = new Date();
      const hoursSinceLastSync = (now.getTime() - lastSyncTime.getTime()) / (1000 * 60 * 60);

      if (hoursSinceLastSync < config.syncInterval) {
        return {
          restaurantId,
          source: actualSource,
          status: 'skipped',
          itemsAdded: 0,
          itemsUpdated: 0,
          itemsRemoved: 0,
          error: `Sync not due yet. Last sync: ${config.lastSync}`,
          timestamp,
        };
      }
    }

    // Fetch external menu data
    const externalData = await fetchExternalMenuData(actualSource, restaurantId);

    if (!externalData || !externalData.success) {
      throw new Error(`Failed to fetch external data: ${externalData?.error || 'Unknown error'}`);
    }

    // Sync data to local database
    const syncStats = await syncMenuData(restaurantId, externalData.data, actualSource);

    // Update sync config
    await updateDoc(configSnapshot.docs[0].ref, {
      lastSync: timestamp,
      updatedAt: timestamp,
    });

    // Log sync result
    const result: SyncResult = {
      restaurantId,
      source: actualSource,
      status: 'success',
      ...syncStats,
      timestamp,
    };

    await addDoc(collection(db, 'sync_logs'), result);

    return result;

  } catch (error) {
    const result: SyncResult = {
      restaurantId,
      source: source || 'unknown',
      status: 'error',
      itemsAdded: 0,
      itemsUpdated: 0,
      itemsRemoved: 0,
      error: error instanceof Error ? error.message : 'Unknown error',
      timestamp,
    };

    // Log error
    await addDoc(collection(db, 'sync_logs'), result);

    return result;
  }
}

// Helper function to fetch external menu data
async function fetchExternalMenuData(source: string, restaurantId: string) {
  try {
    const response = await fetch(`${process.env.NEXTAUTH_URL}/api/fetch-menu`, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({
        source,
        restaurantId,
      }),
    });

    return await response.json();
  } catch (error) {
    console.error('External menu fetch error:', error);
    return null;
  }
}

// Helper function to sync menu data to local database
async function syncMenuData(restaurantId: string, externalItems: any[], source: string) {
  const batch = writeBatch(db);
  let itemsAdded = 0;
  let itemsUpdated = 0;
  let itemsRemoved = 0;

  try {
    // Get existing menu items for this restaurant
    const existingQuery = query(
      collection(db, 'menu_items'),
      where('restaurantId', '==', restaurantId),
      where('source', '==', source),
    );

    const existingSnapshot = await getDocs(existingQuery);
    const existingItems = new Map();

    existingSnapshot.forEach((doc) => {
      const data = doc.data();
      existingItems.set(data.externalId || doc.id, { id: doc.id, ...data });
    });

    // Process external items
    const processedIds = new Set();

    for (const externalItem of externalItems) {
      const externalId = externalItem.id || externalItem.menu_item_id;
      processedIds.add(externalId);

      const menuItem = {
        restaurantId,
        source,
        externalId,
        name: externalItem.name || externalItem.menu_item_name,
        description: externalItem.description || externalItem.menu_item_description,
        price: externalItem.price || 0,
        category: externalItem.category || externalItem.menu_item_category || 'Other',
        image: externalItem.image || externalItem.menu_item_image,
        available: externalItem.available !== false,
        updatedAt: new Date().toISOString(),
        syncedAt: new Date().toISOString(),
      };

      if (existingItems.has(externalId)) {
        // Update existing item
        const existing = existingItems.get(externalId);
        const itemRef = doc(db, 'menu_items', existing.id);
        batch.update(itemRef, menuItem);
        itemsUpdated++;
      } else {
        // Add new item
        const newItemRef = doc(collection(db, 'menu_items'));
        batch.set(newItemRef, {
          ...menuItem,
          createdAt: new Date().toISOString(),
        });
        itemsAdded++;
      }
    }

    // Remove items that are no longer in external source
    for (const [externalId, existing] of existingItems) {
      if (!processedIds.has(externalId)) {
        const itemRef = doc(db, 'menu_items', existing.id);
        batch.delete(itemRef);
        itemsRemoved++;
      }
    }

    // Commit batch
    await batch.commit();

    return { itemsAdded, itemsUpdated, itemsRemoved };

  } catch (error) {
    console.error('Menu sync error:', error);
    throw error;
  }
}

import { NextRequest, NextResponse } from 'next/server';
import { db } from '../../../lib/firebase';
import { collection, doc, getDocs, getDoc, addDoc, updateDoc, query, where, orderBy, limit } from 'firebase/firestore';
import { getAuth } from 'firebase-admin/auth';

export const dynamic = 'force-dynamic';

interface DeliveryRequest {
  orderId: string;
  pickupAddress: string;
  deliveryAddress: string;
  customerPhone: string;
  specialInstructions?: string;
  deliveryType: 'food' | 'package' | 'grocery';
  priority: 'standard' | 'express' | 'urgent';
  estimatedValue?: number;
}

interface DeliveryUpdate {
  status: 'pending' | 'assigned' | 'picked_up' | 'in_transit' | 'delivered' | 'cancelled';
  driverId?: string;
  location?: {
    lat: number;
    lng: number;
    timestamp: string;
  };
  notes?: string;
  estimatedArrival?: string;
}

// Verify authentication
async function verifyAuth(request: NextRequest) {
  const authHeader = request.headers.get('authorization');
  if (!authHeader || !authHeader.startsWith('Bearer ')) {
    throw new Error('Authentication required');
  }

  const token = authHeader.split(' ')[1];

  try {
    const { getAuth } = await import('firebase-admin/auth');
    const decodedToken = await getAuth().verifyIdToken(token);
    return decodedToken.uid;
  } catch (error) {
    throw new Error('Invalid authentication token');
  }
}

// GET - Retrieve delivery information
export async function GET(req: NextRequest) {
  try {
    const userId = await verifyAuth(req);
    const { searchParams } = new URL(req.url);
    const deliveryId = searchParams.get('id');
    const orderId = searchParams.get('orderId');
    const status = searchParams.get('status');
    const driverId = searchParams.get('driverId');

    if (deliveryId) {
      // Get specific delivery
      const deliveryRef = doc(db, 'deliveries', deliveryId);
      const deliveryDoc = await getDoc(deliveryRef);

      if (!deliveryDoc.exists()) {
        return NextResponse.json(
          { error: 'Delivery not found' },
          { status: 404 },
        );
      }

      const deliveryData = deliveryDoc.data();

      // Check if user has access to this delivery
      if (deliveryData.customerId !== userId && deliveryData.driverId !== userId) {
        // Check if user is admin
        const userRef = doc(db, 'users', userId);
        const userDoc = await getDoc(userRef);
        const userData = userDoc.data();

        if (!userData || (userData.role !== 'admin' && userData.role !== 'driver')) {
          return NextResponse.json(
            { error: 'Access denied' },
            { status: 403 },
          );
        }
      }

      return NextResponse.json({
        success: true,
        data: {
          id: deliveryDoc.id,
          ...deliveryData,
        },
      });
    }

    // Get multiple deliveries with filters
    const q = collection(db, 'deliveries');
    const constraints = [];

    if (orderId) {
      constraints.push(where('orderId', '==', orderId));
    }

    if (status) {
      constraints.push(where('status', '==', status));
    }

    if (driverId) {
      constraints.push(where('driverId', '==', driverId));
    } else {
      // If not filtering by driver, show user's deliveries
      constraints.push(where('customerId', '==', userId));
    }

    constraints.push(orderBy('createdAt', 'desc'));
    constraints.push(limit(50));

    const deliveryQuery = query(q, ...constraints);
    const querySnapshot = await getDocs(deliveryQuery);

    const deliveries: any[] = [];
    querySnapshot.forEach((doc) => {
      deliveries.push({
        id: doc.id,
        ...doc.data(),
      });
    });

    return NextResponse.json({
      success: true,
      data: deliveries,
      count: deliveries.length,
    });

  } catch (error) {
    console.error('Delivery GET error:', error);

    if (error instanceof Error && error.message.includes('Authentication')) {
      return NextResponse.json(
        { error: error.message },
        { status: 401 },
      );
    }

    return NextResponse.json(
      { error: 'Internal server error' },
      { status: 500 },
    );
  }
}

// POST - Create new delivery request
export async function POST(req: NextRequest) {
  try {
    const userId = await verifyAuth(req);
    const deliveryRequest: DeliveryRequest = await req.json();

    // Validate required fields
    if (!deliveryRequest.orderId || !deliveryRequest.pickupAddress ||
        !deliveryRequest.deliveryAddress || !deliveryRequest.customerPhone) {
      return NextResponse.json(
        { error: 'Missing required fields' },
        { status: 400 },
      );
    }

    // Calculate estimated delivery time based on priority
    const now = new Date();
    const estimatedDelivery = new Date(now);

    switch (deliveryRequest.priority) {
      case 'urgent':
        estimatedDelivery.setMinutes(now.getMinutes() + 30);
        break;
      case 'express':
        estimatedDelivery.setMinutes(now.getMinutes() + 60);
        break;
      default:
        estimatedDelivery.setMinutes(now.getMinutes() + 90);
    }

    const newDelivery = {
      ...deliveryRequest,
      customerId: userId,
      status: 'pending',
      createdAt: now.toISOString(),
      updatedAt: now.toISOString(),
      estimatedDelivery: estimatedDelivery.toISOString(),
      trackingHistory: [{
        status: 'pending',
        timestamp: now.toISOString(),
        notes: 'Delivery request created',
      }],
    };

    const docRef = await addDoc(collection(db, 'deliveries'), newDelivery);

    return NextResponse.json({
      success: true,
      data: {
        id: docRef.id,
        ...newDelivery,
      },
      message: 'Delivery request created successfully',
    });

  } catch (error) {
    console.error('Delivery POST error:', error);

    if (error instanceof Error && error.message.includes('Authentication')) {
      return NextResponse.json(
        { error: error.message },
        { status: 401 },
      );
    }

    return NextResponse.json(
      { error: 'Internal server error' },
      { status: 500 },
    );
  }
}

// PUT - Update delivery status
export async function PUT(req: NextRequest) {
  try {
    const userId = await verifyAuth(req);
    const { searchParams } = new URL(req.url);
    const deliveryId = searchParams.get('id');

    if (!deliveryId) {
      return NextResponse.json(
        { error: 'Delivery ID required' },
        { status: 400 },
      );
    }

    const update: DeliveryUpdate = await req.json();

    // Check if delivery exists
    const deliveryRef = doc(db, 'deliveries', deliveryId);
    const deliveryDoc = await getDoc(deliveryRef);

    if (!deliveryDoc.exists()) {
      return NextResponse.json(
        { error: 'Delivery not found' },
        { status: 404 },
      );
    }

    const deliveryData = deliveryDoc.data();

    // Check permissions - only driver assigned to delivery or admin can update
    const userRef = doc(db, 'users', userId);
    const userDoc = await getDoc(userRef);
    const userData = userDoc.data();

    const isDriver = deliveryData.driverId === userId;
    const isAdmin = userData?.role === 'admin' || userData?.role === 'super_admin';

    if (!isDriver && !isAdmin) {
      return NextResponse.json(
        { error: 'Access denied' },
        { status: 403 },
      );
    }

    // Update delivery
    const now = new Date().toISOString();
    const trackingHistory = deliveryData.trackingHistory || [];

    // Add new tracking entry
    trackingHistory.push({
      status: update.status,
      timestamp: now,
      notes: update.notes || '',
      location: update.location,
      updatedBy: userId,
    });

    const updatedData = {
      status: update.status,
      updatedAt: now,
      trackingHistory,
      ...(update.driverId && { driverId: update.driverId }),
      ...(update.estimatedArrival && { estimatedArrival: update.estimatedArrival }),
      ...(update.location && { currentLocation: update.location }),
    };

    await updateDoc(deliveryRef, updatedData);

    // Get updated document
    const updatedDoc = await getDoc(deliveryRef);

    return NextResponse.json({
      success: true,
      data: {
        id: updatedDoc.id,
        ...updatedDoc.data(),
      },
      message: 'Delivery updated successfully',
    });

  } catch (error) {
    console.error('Delivery PUT error:', error);

    if (error instanceof Error && error.message.includes('Authentication')) {
      return NextResponse.json(
        { error: error.message },
        { status: 401 },
      );
    }

    return NextResponse.json(
      { error: 'Internal server error' },
      { status: 500 },
    );
  }
}

// DELETE - Cancel delivery (soft delete)
export async function DELETE(req: NextRequest) {
  try {
    const userId = await verifyAuth(req);
    const { searchParams } = new URL(req.url);
    const deliveryId = searchParams.get('id');

    if (!deliveryId) {
      return NextResponse.json(
        { error: 'Delivery ID required' },
        { status: 400 },
      );
    }

    // Check if delivery exists
    const deliveryRef = doc(db, 'deliveries', deliveryId);
    const deliveryDoc = await getDoc(deliveryRef);

    if (!deliveryDoc.exists()) {
      return NextResponse.json(
        { error: 'Delivery not found' },
        { status: 404 },
      );
    }

    const deliveryData = deliveryDoc.data();

    // Check permissions - only customer or admin can cancel
    const userRef = doc(db, 'users', userId);
    const userDoc = await getDoc(userRef);
    const userData = userDoc.data();

    const isCustomer = deliveryData.customerId === userId;
    const isAdmin = userData?.role === 'admin' || userData?.role === 'super_admin';

    if (!isCustomer && !isAdmin) {
      return NextResponse.json(
        { error: 'Access denied' },
        { status: 403 },
      );
    }

    // Check if delivery can be cancelled
    if (deliveryData.status === 'delivered' || deliveryData.status === 'cancelled') {
      return NextResponse.json(
        { error: 'Cannot cancel completed or already cancelled delivery' },
        { status: 400 },
      );
    }

    // Update status to cancelled
    const now = new Date().toISOString();
    const trackingHistory = deliveryData.trackingHistory || [];

    trackingHistory.push({
      status: 'cancelled',
      timestamp: now,
      notes: 'Delivery cancelled by user',
      updatedBy: userId,
    });

    await updateDoc(deliveryRef, {
      status: 'cancelled',
      updatedAt: now,
      trackingHistory,
      cancelledAt: now,
      cancelledBy: userId,
    });

    return NextResponse.json({
      success: true,
      message: 'Delivery cancelled successfully',
    });

  } catch (error) {
    console.error('Delivery DELETE error:', error);

    if (error instanceof Error && error.message.includes('Authentication')) {
      return NextResponse.json(
        { error: error.message },
        { status: 401 },
      );
    }

    return NextResponse.json(
      { error: 'Internal server error' },
      { status: 500 },
    );
  }
}

