import { NextRequest, NextResponse } from 'next/server';
import { writeFile, mkdir } from 'fs/promises';
import { join } from 'path';
import { existsSync } from 'fs';
import { adminDb } from '../../../../lib/firebaseAdmin';

export async function POST(request: NextRequest) {
  try {
    const formData = await request.formData();

    // Extract form fields
    const customerName = formData.get('customerName') as string;
    const customerPhone = formData.get('customerPhone') as string;
    const customerEmail = formData.get('customerEmail') as string;
    const restaurantName = formData.get('restaurantName') as string;
    const pickupLocation = formData.get('pickupLocation') as string;
    const estimatedTotal = formData.get('estimatedTotal') as string;
    const specialInstructions = formData.get('specialInstructions') as string;
    const screenshot = formData.get('screenshot') as File;

    // Validate required fields
    if (!customerName || !customerPhone || !customerEmail || !restaurantName || !pickupLocation || !estimatedTotal || !screenshot) {
      return NextResponse.json(
        { error: 'Missing required fields' },
        { status: 400 },
      );
    }

    // Validate file type
    if (!screenshot.type.startsWith('image/')) {
      return NextResponse.json(
        { error: 'Invalid file type. Please upload an image.' },
        { status: 400 },
      );
    }

    // Generate unique order ID
    const orderId = `SS-${Date.now()}-${Math.floor(Math.random() * 1000).toString().padStart(3, '0')}`;

    // Create uploads directory if it doesn't exist
    const uploadsDir = join(process.cwd(), 'public', 'uploads', 'screenshots');
    if (!existsSync(uploadsDir)) {
      await mkdir(uploadsDir, { recursive: true });
    }

    // Save screenshot file
    const bytes = await screenshot.arrayBuffer();
    const buffer = Buffer.from(bytes);
    const fileExtension = screenshot.name.split('.').pop() || 'jpg';
    const fileName = `${orderId}.${fileExtension}`;
    const filePath = join(uploadsDir, fileName);

    await writeFile(filePath, buffer);
    const screenshotUrl = `/uploads/screenshots/${fileName}`;

    // Create order data
    const orderData = {
      orderId,
      type: 'screenshot',
      status: 'pending_review',
      customerInfo: {
        name: customerName,
        phone: customerPhone,
        email: customerEmail,
      },
      restaurantInfo: {
        name: restaurantName,
        pickupLocation: pickupLocation,
      },
      orderDetails: {
        estimatedTotal: estimatedTotal,
        specialInstructions: specialInstructions || '',
        screenshotUrl: screenshotUrl,
        originalFileName: screenshot.name,
      },
      timestamps: {
        created: new Date().toISOString(),
        updated: new Date().toISOString(),
      },
      workflow: {
        reviewRequired: true,
        confirmationCalled: false,
        orderPlaced: false,
        pickedUp: false,
        delivered: false,
      },
      metadata: {
        userAgent: request.headers.get('user-agent') || '',
        ipAddress: request.headers.get('x-forwarded-for') || request.headers.get('x-real-ip') || 'unknown',
      },
    };

    // Save to Firestore
    const docRef = await adminDb.collection('screenshot_orders').add(orderData);

    // Update order with Firestore document ID
    await adminDb.collection('screenshot_orders').doc(docRef.id).update({
      firestoreId: docRef.id,
    });

    // TODO: Send notification to admin team
    // This could be an email, Slack notification, or push notification
    // For now, we'll log it
    console.log(`New screenshot order received: ${orderId}`);
    console.log(`Customer: ${customerName} (${customerPhone})`);
    console.log(`Restaurant: ${restaurantName}`);
    console.log(`Estimated Total: ${estimatedTotal}`);

    // TODO: Send confirmation email to customer
    // This would typically use a service like SendGrid, Mailgun, etc.

    return NextResponse.json({
      success: true,
      orderId: orderId,
      message: 'Screenshot order submitted successfully',
    });

  } catch (error) {
    console.error('Error processing screenshot order:', error);
    return NextResponse.json(
      { error: 'Internal server error' },
      { status: 500 },
    );
  }
}

// Handle GET requests to retrieve screenshot orders (for admin)
export async function GET(request: NextRequest) {
  try {
    const { searchParams } = new URL(request.url);
    const orderId = searchParams.get('orderId');
    const status = searchParams.get('status');
    const limit = parseInt(searchParams.get('limit') || '50');

    let query = adminDb.collection('screenshot_orders');

    // Filter by order ID if provided
    if (orderId) {
      const doc = await query.doc(orderId).get();
      if (!doc.exists) {
        return NextResponse.json(
          { error: 'Order not found' },
          { status: 404 },
        );
      }
      return NextResponse.json({
        order: { id: doc.id, ...doc.data() },
      });
    }

    // Filter by status if provided
    if (status) {
      query = query.where('status', '==', status);
    }

    // Order by creation date (newest first) and limit results
    const snapshot = await query
      .orderBy('timestamps.created', 'desc')
      .limit(limit)
      .get();

    const orders = snapshot.docs.map(doc => ({
      id: doc.id,
      ...doc.data(),
    }));

    return NextResponse.json({
      orders,
      count: orders.length,
    });

  } catch (error) {
    console.error('Error retrieving screenshot orders:', error);
    return NextResponse.json(
      { error: 'Internal server error' },
      { status: 500 },
    );
  }
}

// Handle PUT requests to update order status
export async function PUT(request: NextRequest) {
  try {
    const { orderId, status, notes, adminId } = await request.json();

    if (!orderId || !status) {
      return NextResponse.json(
        { error: 'Order ID and status are required' },
        { status: 400 },
      );
    }

    const validStatuses = [
      'pending_review',
      'confirmed',
      'order_placed',
      'picked_up',
      'out_for_delivery',
      'delivered',
      'cancelled',
      'failed',
    ];

    if (!validStatuses.includes(status)) {
      return NextResponse.json(
        { error: 'Invalid status' },
        { status: 400 },
      );
    }

    // Update order in Firestore
    const updateData: any = {
      status,
      'timestamps.updated': new Date().toISOString(),
    };

    if (notes) {
      updateData.adminNotes = notes;
    }

    if (adminId) {
      updateData.lastUpdatedBy = adminId;
    }

    // Update workflow flags based on status
    switch (status) {
      case 'confirmed':
        updateData['workflow.confirmationCalled'] = true;
        break;
      case 'order_placed':
        updateData['workflow.orderPlaced'] = true;
        break;
      case 'picked_up':
        updateData['workflow.pickedUp'] = true;
        break;
      case 'delivered':
        updateData['workflow.delivered'] = true;
        break;
    }

    await adminDb.collection('screenshot_orders').doc(orderId).update(updateData);

    return NextResponse.json({
      success: true,
      message: 'Order status updated successfully',
    });

  } catch (error) {
    console.error('Error updating screenshot order:', error);
    return NextResponse.json(
      { error: 'Internal server error' },
      { status: 500 },
    );
  }
}

import { NextRequest, NextResponse } from 'next/server';
import { adminAuth, adminDb } from '@/lib/firebaseAdmin';
import Stripe from 'stripe';
import { v4 as uuidv4 } from 'uuid';

const stripe = new Stripe(process.env.STRIPE_SECRET_KEY!, {
  apiVersion: '2024-06-20',
});

interface ServiceDetails {
  type: 'grocery' | 'rides' | 'package';
  title: string;
  description: string;
  estimatedPrice: number;
  serviceDetails: any;
}

interface CustomerInfo {
  name: string;
  phone: string;
  email: string;
  address: string;
  specialInstructions: string;
}

export async function POST(request: NextRequest) {
  try {
    // Verify Firebase Admin is initialized
    if (!adminAuth || !adminDb) {
      console.error('Firebase Admin not initialized');
      return NextResponse.json(
        { error: 'Server configuration error' },
        { status: 500 },
      );
    }

    // Verify Stripe is configured
    if (!process.env.STRIPE_SECRET_KEY) {
      console.error('Stripe secret key not configured');
      return NextResponse.json(
        { error: 'Payment processing not configured' },
        { status: 500 },
      );
    }

    // Get and verify the authorization token
    const authHeader = request.headers.get('authorization');
    let userId = null;

    if (authHeader && authHeader.startsWith('Bearer ')) {
      try {
        const token = authHeader.substring(7);
        const decodedToken = await adminAuth.verifyIdToken(token);
        userId = decodedToken.uid;
      } catch (error) {
        console.error('Token verification failed:', error);
        // Continue without user ID for guest checkout
      }
    }

    // Parse request body
    const { serviceDetails, customerInfo, amount }: {
      serviceDetails: ServiceDetails;
      customerInfo: CustomerInfo;
      amount: number;
    } = await request.json();

    // Validate required fields
    if (!serviceDetails || !customerInfo || !amount) {
      return NextResponse.json(
        { error: 'Missing required checkout information' },
        { status: 400 },
      );
    }

    // Validate amount
    if (typeof amount !== 'number' || amount <= 0) {
      return NextResponse.json(
        { error: 'Invalid amount' },
        { status: 400 },
      );
    }

    // Validate customer information
    const { name, phone, email, address } = customerInfo;
    if (!name || !phone || !email || !address) {
      return NextResponse.json(
        { error: 'Missing required customer information' },
        { status: 400 },
      );
    }

    // Generate order ID for tracking
    const orderId = `OTW-${Date.now()}-${uuidv4().substring(0, 8).toUpperCase()}`;

    // Create Stripe checkout session
    const session = await stripe.checkout.sessions.create({
      payment_method_types: ['card'],
      line_items: [
        {
          price_data: {
            currency: 'usd',
            product_data: {
              name: serviceDetails.title,
              description: serviceDetails.description,
              metadata: {
                service_type: serviceDetails.type,
                order_id: orderId,
              },
            },
            unit_amount: amount,
          },
          quantity: 1,
        },
      ],
      mode: 'payment',
      success_url: `${process.env.NEXT_PUBLIC_BASE_URL}/otw/checkout/success?session_id={CHECKOUT_SESSION_ID}&order_id=${orderId}`,
      cancel_url: `${process.env.NEXT_PUBLIC_BASE_URL}/otw/checkout?service=${serviceDetails.type}`,
      customer_email: customerInfo.email,
      metadata: {
        order_id: orderId,
        user_id: userId || 'guest',
        service_type: serviceDetails.type,
        service_title: serviceDetails.title,
        customer_name: customerInfo.name,
        customer_phone: customerInfo.phone,
        customer_address: customerInfo.address,
        special_instructions: customerInfo.specialInstructions || '',
        source: 'otw_web',
      },
      billing_address_collection: 'auto',
      shipping_address_collection: {
        allowed_countries: ['US', 'CA'],
      },
      phone_number_collection: {
        enabled: true,
      },
      custom_fields: [
        {
          key: 'service_address',
          label: {
            type: 'custom',
            custom: 'Service Address',
          },
          type: 'text',
          text: {
            default_value: customerInfo.address,
          },
        },
      ],
    });

    // Create pending order record
    const orderData = {
      orderId,
      userId: userId || null,
      serviceDetails,
      customerInfo,
      paymentMethod: 'card',
      paymentStatus: 'pending',
      orderStatus: 'pending_payment',
      estimatedPrice: serviceDetails.estimatedPrice,
      actualPrice: amount / 100, // Convert from cents
      stripeSessionId: session.id,
      createdAt: new Date().toISOString(),
      updatedAt: new Date().toISOString(),
      assignedHelper: null,
      completedAt: null,
      notes: [],
      metadata: {
        source: 'otw_web',
        userAgent: request.headers.get('user-agent') || '',
        ip: request.headers.get('x-forwarded-for') || request.headers.get('x-real-ip') || 'unknown',
      },
    };

    // Save pending order to Firestore
    try {
      await adminDb.collection('otw_orders').doc(orderId).set(orderData);

      // Also save to user's orders if authenticated
      if (userId) {
        await adminDb.collection('users').doc(userId).collection('otw_orders').doc(orderId).set({
          orderId,
          serviceType: serviceDetails.type,
          serviceTitle: serviceDetails.title,
          estimatedPrice: serviceDetails.estimatedPrice,
          paymentMethod: 'card',
          orderStatus: 'pending_payment',
          stripeSessionId: session.id,
          createdAt: new Date().toISOString(),
        });
      }

      console.log(`OTW Stripe session created: ${session.id} for order: ${orderId}`);

      return NextResponse.json({
        sessionId: session.id,
        url: session.url,
        orderId,
      });
    } catch (firestoreError) {
      console.error('Firestore error:', firestoreError);
      return NextResponse.json(
        { error: 'Failed to create order record' },
        { status: 500 },
      );
    }
  } catch (error) {
    console.error('Stripe checkout session creation error:', error);
    return NextResponse.json(
      { error: 'Failed to create checkout session' },
      { status: 500 },
    );
  }
}

