      return NextResponse.json(
        { success: false, error: 'Missing required fields' },
        { status: 400 },
      );
    }

    // Create package delivery request
    const packageData = {
      ...body,
      status: 'pending',
      trackingNumber: `PKG${Date.now()}`,
      createdAt: new Date().toISOString(),
      updatedAt: new Date().toISOString(),
