export default function CheckoutPage() {
  const router = useRouter();
  const { items, subtotal, tax, total, clearCart } = useCart();
  // Allow step = 1, 2, or 3
  const [step, setStep] = useState<1 | 2 | 3>(1);
  const [isProcessing, setIsProcessing] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const [orderComplete, setOrderComplete] = useState(false);
  const [orderId, setOrderId] = useState<string | null>(null);
  const [selected, setSelected] = useState<PlaceSuggestion | null>(null);

  // Form state
  const [formData, setFormData] = useState({
    // Contact Information
    email: '',
    phone: '',

    // Delivery Information
    firstName: '',
    lastName: '',
    address: '',
    apartment: '',
    city: '',
    state: '',
    zipCode: '',
    deliveryInstructions: '',

    // Payment Information
    paymentMethod: 'card' as 'card' | 'cash',
    cardNumber: '',
    cardName: '',
    expiryDate: '',
    cvv: '',

    // Order Type
    orderType: 'delivery' as 'delivery' | 'pickup',

    // Pickup Location (if pickup selected)
    pickupLocation: '',

    // Delivery Options
    deliveryTime: 'asap' as 'asap' | 'scheduled',
    scheduledTime: '',

    // Promo Code
    promoCode: '',

    // Terms
    agreeToTerms: false,
  });

  // Type definitions for cart items
  type CustomizationOption = {
    name: string;
    value: string;
  };

  type CartItem = {
    id: string;
    name: string;
    price: number;
    quantity: number;
    customizations?: CustomizationOption[];
    image?: string;
  };

  // Format customizations for display
  const formatCustomizations = (item: CartItem) => {
    if (!item.customizations?.length) {return '';}
    return `Customizations: ${item.customizations
      .map((c) => `${c.name}: ${c.value}`)
      .join(', ')}`;
  };

  // Redirect to cart if cart is empty
  useEffect(() => {
    if (items.length === 0 && !orderComplete) {
      router.push('/cart');
    }
  }, [items, router, orderComplete]);

  // Handle form input changes
  const handleChange = (
    e: React.ChangeEvent<
      HTMLInputElement | HTMLTextAreaElement | HTMLSelectElement
    >,
  ) => {
    const { name, value, type } = e.target;
    const checked =
      type === 'checkbox' ? (e.target as HTMLInputElement).checked : undefined;

    setFormData({
      ...formData,
      [name]: type === 'checkbox' ? checked : value,
    });
  };

  // Handle order type change
  const handleOrderTypeChange = (type: 'delivery' | 'pickup') => {
    setFormData({
      ...formData,
      orderType: type,
    });
  };

  // Handle form submission
  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    // Validate form based on current step
    if (!validateForm()) {
      return;
    }

    // If not yet on final step, move forward
    if (step < 3) {
      setStep((prev) => (prev + 1) as 1 | 2 | 3);
      window.scrollTo(0, 0);
      return;
    }

    // Now step === 3 â†’ Process payment and complete order
    setIsProcessing(true);
    setError(null);

    try {
      // ====== CASH ON ARRIVAL =========
      if (formData.paymentMethod === 'cash') {
