'use client';

export const dynamic = 'force-dynamic';

import type { Metadata } from 'next';
import { useState, useEffect } from 'react';
import { Button } from '../../components/ui/button';
import { Input } from '../../components/ui/input';
import { Label } from '../../components/ui/label';
import {
  Card,
  CardContent,
  CardDescription,
  CardFooter,
  CardHeader,
  CardTitle,
} from '../../components/ui/card';
import {
  Avatar,
  AvatarFallback,
  AvatarImage,
} from '../../components/ui/avatar';
import { Badge } from '../../components/ui/badge';
import { User, CreditCard, MapPin, Bell, Shield, LogOut } from 'lucide-react';
import Link from 'next/link';
import { useAuth } from '../../contexts/AuthContext';
import { doc, getDoc, collection, query, where, getDocs } from 'firebase/firestore';
import { db } from '../../lib/firebase-config';
import type { UserProfile } from '../../types';

interface Address {
  id: string;
  name: string;
  street: string;
  city: string;
  state: string;
  zip: string;
  default: boolean;
}

interface PaymentMethod {
  id: string;
  type: string;
  last4: string;
  expiry: string;
  default: boolean;
}

interface UserData {
  name: string;
  email: string;
  phone?: string;
  avatar?: string;
  memberSince: string;
  tier: string;
  rewardPoints: number;
  addresses: Address[];
  paymentMethods: PaymentMethod[];
}

export default function ProfilePage() {
  const [activeTab, setActiveTab] = useState('account');
  const [userData, setUserData] = useState<UserData | null>(null);
  const [loading, setLoading] = useState(true);
  const { user, logout } = useAuth();

  const handleSignOut = async () => {
    try {
      await logout();
    } catch (error) {
      console.error('Error signing out:', error);
    }
  };

  // Fetch user profile data from Firebase
  useEffect(() => {
    const fetchUserData = async () => {
      if (!user?.uid) {
        setLoading(false);
        return;
      }

      try {
        // Fetch user profile from Firestore
        const userDocRef = doc(db, 'users', user.uid);
        const userDoc = await getDoc(userDocRef);

        let profileData: Partial<UserProfile> = {};
        if (userDoc.exists()) {
          profileData = userDoc.data() as UserProfile;
        }

        // Fetch user addresses
        const addressesQuery = query(
          collection(db, 'addresses'),
          where('userId', '==', user.uid),
        );
        const addressesSnapshot = await getDocs(addressesQuery);
        const addresses: Address[] = addressesSnapshot.docs.map(doc => ({
          id: doc.id,
          ...doc.data(),
        })) as Address[];

        // Fetch user payment methods
        const paymentMethodsQuery = query(
          collection(db, 'paymentMethods'),
          where('userId', '==', user.uid),
        );
        const paymentMethodsSnapshot = await getDocs(paymentMethodsQuery);
        const paymentMethods: PaymentMethod[] = paymentMethodsSnapshot.docs.map(doc => ({
          id: doc.id,
          ...doc.data(),
        })) as PaymentMethod[];

        // Calculate member since date
        const memberSince = profileData.createdAt
          ? new Date(profileData.createdAt).toLocaleDateString('en-US', {
              year: 'numeric',
              month: 'long',
            })
          : 'Recently';

        // Determine tier based on reward points
        const points = profileData.rewardPoints || 0;
        let tier = 'Bronze';
        if (points >= 1000) {tier = 'Gold';}
        else if (points >= 500) {tier = 'Silver';}

        setUserData({
          name: user.displayName || profileData.displayName || 'User',
          email: user.email || profileData.email || '',
          phone: profileData.phone || '',
          avatar: user.photoURL || profileData.photoURL || '',
          memberSince,
          tier,
          rewardPoints: points,
          addresses,
          paymentMethods,
        });
      } catch (error) {
        console.error('Error fetching user data:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchUserData();
  }, [user]);

  if (loading) {
    return (
      <div className="min-h-screen pb-20 pt-24 flex items-center justify-center">
        <div className="text-center">
          <div className="animate-spin rounded-full h-32 w-32 border-b-2 border-otw-gold mx-auto mb-4"></div>
          <p className="text-lg">Loading your profile...</p>
        </div>
      </div>
    );
  }

  if (!user || !userData) {
    return (
      <div className="min-h-screen pb-20 pt-24 flex items-center justify-center">
        <div className="text-center">
          <p className="text-lg mb-4">Please sign in to view your profile.</p>
          <Link href="/auth/signin">
            <Button>Sign In</Button>
          </Link>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen pb-20 pt-24">
      <div className="container mx-auto px-4">
        <div className="grid grid-cols-1 lg:grid-cols-4 gap-8">
          {/* Sidebar */}
          <div className="lg:col-span-1">
            <Card>
              <CardHeader>
                <div className="flex flex-col items-center">
                  <Avatar className="h-24 w-24 mb-4">
                    <AvatarImage
                      src={userData.avatar || '/assets/users/default-avatar.jpg'}
                      alt={userData.name}
                    />
