              <p className="text-sm text-gray-300">
                Don't have an account?{' '}
                <Link href="/signup" className="text-otw-gold hover:underline">
                  Sign up
                </Link>
              </p>
            </div>
          </CardFooter>
        </form>
      </Card>
    </div>
  );
}

