'use client';

import { useState } from 'react';
import { Card, CardContent, CardHeader, CardTitle } from '../../components/ui/card';
import { Input } from '../../components/ui/input';
import { Button } from '../../components/ui/button';
import { Alert, AlertDescription } from '../../components/ui/alert';
import { CheckCircle, Clock, MapPin, User, AlertCircle } from 'lucide-react';

const statusSteps = [
  {
    id: 'received',
    title: 'Order Received',
    icon: Clock,
    description: "We've received your order",
  },
  {
    id: 'confirmed',
    title: 'Order Confirmed',
    icon: CheckCircle,
    description: 'Your order has been confirmed',
  },
  {
    id: 'preparing',
    title: 'Preparing',
    icon: User,
    description: 'Your order is being prepared',
  },
  {
    id: 'out_for_delivery',
    title: 'Out for Delivery',
    icon: MapPin,
    description: 'Your order is on the way',
  },
  {
    id: 'delivered',
    title: 'Delivered',
    icon: CheckCircle,
    description: 'Order delivered successfully',
  },
];

export default function TrackPage() {
  const [orderId, setOrderId] = useState('');
  const [email, setEmail] = useState('');
  const [orderData, setOrderData] = useState<any>(null);
  const [error, setError] = useState('');
  const [isLoading, setIsLoading] = useState(false);

  const handleTrack = async (e: React.FormEvent) => {
    e.preventDefault();
    setError('');
    setOrderData(null);

    if (!orderId.trim() || !email.trim()) {
      setError('Please enter both Order ID and email address');
      return;
    }

    setIsLoading(true);

    try {
      // Mock verification - replace with actual API call
      // This would verify the order belongs to the customer
      const mockOrderData = {
        orderId: orderId,
        customerEmail: email,
        status: 'preparing',
        estimatedDelivery: '7:30 PM',
        items: ['2x Pepperoni Pizza', '1x Garlic Bread'],
        total: 28.99,
        address: '123 Main St, Fort Wayne, IN',
      };

      // Simulate API delay
      await new Promise(resolve => setTimeout(resolve, 1000));

      // Mock validation - in real implementation, verify email matches order
      if (email.toLowerCase() === 'test@example.com' || email.includes('@')) {
        setOrderData(mockOrderData);
      } else {
        setError('Order not found or email does not match our records');
      }
    } catch (error) {
      setError('Unable to track order. Please try again later.');
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="min-h-screen bg-black text-white p-6">
      <div className="max-w-4xl mx-auto">
        <div className="mb-8">
          <h1 className="text-4xl font-bold text-otw-gold mb-2">
            Track Your Order
          </h1>
          <p className="text-lg text-gray-300">
            Enter your order details to track your delivery in real-time.
          </p>
        </div>

        <Card className="bg-gray-900 border-gray-800 mb-8">
          <CardHeader>
            <CardTitle className="text-white">Order Verification</CardTitle>
          </CardHeader>
          <CardContent>
            <form onSubmit={handleTrack} className="space-y-4">
              <div>
                <label className="block text-sm font-medium text-gray-300 mb-2">
                  Order ID
                </label>
                <Input
                  value={orderId}
                  onChange={(e) => setOrderId(e.target.value)}
                  placeholder="Enter your order ID (e.g., OTW-2024-001)"
                  className="bg-gray-800 border-gray-700 text-white"
                  required
                />
              </div>
              <div>
                <label className="block text-sm font-medium text-gray-300 mb-2">
                  Email Address
                </label>
                <Input
                  type="email"
                  value={email}
                  onChange={(e) => setEmail(e.target.value)}
                  placeholder="Enter the email used for this order"
                  className="bg-gray-800 border-gray-700 text-white"
                  required
                />
              </div>
              {error && (
                <Alert className="bg-red-900/20 border-red-800">
                  <AlertCircle className="h-4 w-4" />
                  <AlertDescription className="text-red-400">
                    {error}
                  </AlertDescription>
                </Alert>
              )}
              <Button
                type="submit"
                disabled={isLoading}
                className="w-full bg-otw-gold hover:bg-yellow-600 text-black font-semibold"
              >
                {isLoading ? 'Tracking...' : 'Track Order'}
              </Button>
            </form>
          </CardContent>
        </Card>

        {orderData && (
          <div className="space-y-6">
            <Card className="bg-gray-900 border-gray-800">
              <CardHeader>
                <CardTitle className="text-white">Order Details</CardTitle>
              </CardHeader>
              <CardContent>
                <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                  <div>
                    <p className="text-gray-400">Order ID</p>
                    <p className="text-white font-semibold">{orderData.orderId}</p>
                  </div>
                  <div>
                    <p className="text-gray-400">Estimated Delivery</p>
                    <p className="text-white font-semibold">{orderData.estimatedDelivery}</p>
                  </div>
                  <div>
                    <p className="text-gray-400">Total</p>
                    <p className="text-white font-semibold">${orderData.total}</p>
                  </div>
                  <div>
                    <p className="text-gray-400">Delivery Address</p>
                    <p className="text-white font-semibold">{orderData.address}</p>
                  </div>
                </div>
                <div className="mt-4">
                  <p className="text-gray-400 mb-2">Items</p>
                  <div className="space-y-1">
                    {orderData.items.map((item: string, index: number) => (
                      <p key={index} className="text-white">{item}</p>
                    ))}
                  </div>
                </div>
              </CardContent>
            </Card>

            <Card className="bg-gray-900 border-gray-800">
              <CardHeader>
                <CardTitle className="text-white">Order Status</CardTitle>
              </CardHeader>
              <CardContent>
                <div className="relative">
                  <div className="absolute left-8 top-0 h-full w-0.5 bg-gray-700" />
                  <div className="space-y-8">
                    {statusSteps.map((step) => {
                      const isActive = statusSteps
                        .slice(0, statusSteps.findIndex((s) => s.id === orderData.status) + 1)
                        .map((s) => s.id)
                        .includes(step.id);

                      return (
                        <div key={step.id} className="relative flex items-start gap-4">
                          <div
                            className={`relative z-10 rounded-full p-2 ${
                              isActive ? 'bg-otw-gold text-black' : 'bg-gray-800 text-gray-400'
                            }`}
                          >
                            <step.icon className="w-4 h-4" />
                          </div>
                          <div>
                            <h3
                              className={`font-semibold ${
                                isActive ? 'text-otw-gold' : 'text-gray-400'
                              }`}
                            >
                              {step.title}
                            </h3>
                            <p className="text-sm text-gray-500">{step.description}</p>
                          </div>
                        </div>
                      );
                    })}
                  </div>
                </div>
              </CardContent>
            </Card>
          </div>
        )}
      </div>
    </div>
  );
}

import React, { useRef, useEffect } from 'react';
import { Wrapper } from '@googlemaps/react-wrapper';

interface PlaceDetails {
  formatted_address: string;
  place_id: string;
  geometry: {
    location: {
      lat: () => number;
      lng: () => number;
    };
  };
  address_components: google.maps.GeocoderAddressComponent[];
  name?: string;
}

type ThemeVariant = 'default' | 'modern' | 'minimal' | 'glassmorphism' | 'dark';
type SizeVariant = 'sm' | 'md' | 'lg' | 'xl';

interface StyleConfig {
  container?: string;
  input?: string;
  loading?: string;
  error?: string;
  icon?: string;
}

interface AddressSearchProps {
  onPlaceSelect: (place: PlaceDetails) => void;
  placeholder?: string;
  className?: string;
  apiKey: string;
  theme?: ThemeVariant;
  size?: SizeVariant;
  disabled?: boolean;
  showIcon?: boolean;
  customStyles?: StyleConfig;
  borderRadius?: 'none' | 'sm' | 'md' | 'lg' | 'xl' | 'full';
  focusColor?: string;
  errorMessage?: string;
  loadingText?: string;
}

// Theme configurations
const getThemeStyles = (theme: ThemeVariant = 'default', focusColor?: string): string => {
  const themes = {
    default: 'border-gray-300 bg-white text-gray-900 focus:ring-blue-500 focus:border-blue-500',
    modern: 'border-gray-200 bg-gray-50 text-gray-900 focus:ring-indigo-500 focus:border-indigo-500 shadow-sm',
    minimal: 'border-gray-200 bg-transparent text-gray-900 focus:ring-gray-400 focus:border-gray-400 border-b-2 border-t-0 border-l-0 border-r-0 rounded-none',
    glassmorphism: 'border-white/20 bg-white/10 backdrop-blur-md text-gray-900 focus:ring-white/30 focus:border-white/30 shadow-lg',
    dark: 'border-gray-600 bg-gray-800 text-white focus:ring-gray-400 focus:border-gray-400 placeholder-gray-400',
  };

  let baseStyle = themes[theme];

  if (focusColor) {
    baseStyle = baseStyle.replace(/focus:ring-\w+-\d+/, `focus:ring-[${focusColor}]`);
    baseStyle = baseStyle.replace(/focus:border-\w+-\d+/, `focus:border-[${focusColor}]`);
  }

  return baseStyle;
};

const getSizeStyles = (size: SizeVariant = 'md'): string => {
  const sizes = {
    sm: 'px-3 py-1.5 text-sm',
    md: 'px-4 py-2 text-base',
    lg: 'px-5 py-3 text-lg',
    xl: 'px-6 py-4 text-xl',
  };
  return sizes[size];
};

const getBorderRadiusStyles = (borderRadius: AddressSearchProps['borderRadius'] = 'md'): string => {
  const radiusMap = {
    none: 'rounded-none',
    sm: 'rounded-sm',
    md: 'rounded-md',
    lg: 'rounded-lg',
    xl: 'rounded-xl',
    full: 'rounded-full',
  };
  return radiusMap[borderRadius];
};

const AutocompleteInput: React.FC<Omit<AddressSearchProps, 'apiKey'>> = ({
  onPlaceSelect,
  placeholder = 'Enter an address...',
  className = '',
  theme = 'default',
  size = 'md',
  disabled = false,
  showIcon = true,
  customStyles,
  borderRadius = 'md',
  focusColor,
}) => {
  const containerRef = useRef<HTMLDivElement>(null);
  const autocompleteRef = useRef<any>(null);

  useEffect(() => {
    if (!containerRef.current || !window.google) {return;}

    // Initialize the new PlaceAutocompleteElement
    const autocompleteElement = new window.google.maps.places.PlaceAutocompleteElement({
      types: ['address'],
      fields: [
        'formattedAddress',
        'id',
        'location',
        'addressComponents',
        'displayName',
      ],
    });

    // Configure the autocomplete element
    autocompleteElement.placeholder = placeholder;
    if (disabled) {
      autocompleteElement.setAttribute('disabled', 'true');
    }

    // Apply custom styling to the autocomplete element
    const inputStyles = [
      'w-full',
      'outline-none',
      'transition-all duration-200',
      'focus:ring-2',
      getSizeStyles(size),
      getThemeStyles(theme, focusColor),
      getBorderRadiusStyles(borderRadius),
      disabled ? 'opacity-50 cursor-not-allowed' : 'cursor-text',
      customStyles?.input || '',
      className,
      showIcon ? 'pl-10' : '',
    ].filter(Boolean).join(' ');

    autocompleteElement.className = inputStyles;

    // Add the element to the container
    containerRef.current.appendChild(autocompleteElement);

    // Add place changed listener
    const handlePlaceSelect = (event: any) => {
      const place = event.place;

      if (place && place.location) {
        const placeDetails: PlaceDetails = {
          formatted_address: place.formattedAddress || '',
          place_id: place.id || '',
          geometry: {
            location: {
              lat: () => place.location.lat(),
              lng: () => place.location.lng(),
            },
          },
          address_components: place.addressComponents || [],
          name: place.displayName,
        };

        onPlaceSelect(placeDetails);
      }
    };

    autocompleteElement.addEventListener('gmp-placeselect', handlePlaceSelect);

    // Store reference for cleanup
    autocompleteRef.current = autocompleteElement;

    // Cleanup
    return () => {
      if (autocompleteElement) {
        autocompleteElement.removeEventListener('gmp-placeselect', handlePlaceSelect);
        if (autocompleteElement.parentNode) {
          autocompleteElement.parentNode.removeChild(autocompleteElement);
        }
      }
    };
  }, [onPlaceSelect, placeholder, disabled, theme, size, borderRadius, focusColor, className, customStyles, showIcon]);

  // Location icon SVG
  const LocationIcon = () => (
    <svg
      className={`w-5 h-5 ${theme === 'dark' ? 'text-gray-400' : 'text-gray-500'} ${customStyles?.icon || ''}`}
      fill="none"
      stroke="currentColor"
      viewBox="0 0 24 24"
    >
      <path
        strokeLinecap="round"
        strokeLinejoin="round"
        strokeWidth={2}
        d="M17.657 16.657L13.414 20.9a1.998 1.998 0 01-2.827 0l-4.244-4.243a8 8 0 1111.314 0z"
      />
      <path
        strokeLinecap="round"
        strokeLinejoin="round"
        strokeWidth={2}
        d="M15 11a3 3 0 11-6 0 3 3 0 016 0z"
      />
    </svg>
  );

  return (
    <div className={`relative ${customStyles?.container || ''}`}>
      {showIcon && (
        <div className={'absolute left-3 top-1/2 transform -translate-y-1/2 pointer-events-none z-10'}>
          <LocationIcon />
        </div>
      )}
      <div ref={containerRef} className="w-full" />
    </div>
  );
};

const AddressSearch: React.FC<AddressSearchProps> = ({
  onPlaceSelect,
  placeholder,
  className,
  apiKey,
  theme = 'default',
  size = 'md',
  disabled = false,
  showIcon = true,
  customStyles,
  borderRadius = 'md',
  focusColor,
  errorMessage,
  loadingText = 'Loading Google Maps...',
}) => {
  const render = (status: string) => {
    if (status === 'LOADING') {
      const loadingStyles = [
        'flex items-center justify-center',
        getSizeStyles(size),
        getThemeStyles(theme),
        getBorderRadiusStyles(borderRadius),
        'border',
        customStyles?.loading || '',
      ].filter(Boolean).join(' ');

      return (
        <div className={loadingStyles}>
          <div className={`animate-spin rounded-full h-5 w-5 border-b-2 ${
            theme === 'dark' ? 'border-white' : 'border-blue-500'
          }`}></div>
          <span className={`ml-2 ${
            theme === 'dark' ? 'text-gray-300' : 'text-gray-600'
          }`}>{loadingText}</span>
        </div>
      );
    }

    if (status === 'FAILURE') {
      const errorStyles = [
        getSizeStyles(size),
        getBorderRadiusStyles(borderRadius),
        theme === 'dark'
          ? 'bg-red-900/20 border-red-500/30 text-red-300'
          : 'bg-red-50 border-red-200 text-red-600',
        'border',
        customStyles?.error || '',
      ].filter(Boolean).join(' ');

      return (
        <div className={errorStyles}>
          <p>{errorMessage || 'Failed to load Google Maps. Please check your API key.'}</p>
        </div>
      );
    }

    return (
      <AutocompleteInput
        onPlaceSelect={onPlaceSelect}
        placeholder={placeholder}
        className={className}
        theme={theme}
        size={size}
        disabled={disabled}
        showIcon={showIcon}
        customStyles={customStyles}
        borderRadius={borderRadius}
        focusColor={focusColor}
      />
    );
  };

  return (
    <Wrapper
      apiKey={apiKey}
      libraries={['places']}
      render={render}
    />
  );
};

export default AddressSearch;
export type { PlaceDetails, AddressSearchProps };

'use client';

import { useState, PropsWithChildren } from 'react';
import Link from 'next/link';

interface ButtonProps extends React.ButtonHTMLAttributes<HTMLButtonElement> {
  href?: string;
  variant?: 'primary' | 'secondary';
  disabled?: boolean;
  fullWidth?: boolean;
  glow?: boolean;
}

export default function EnhancedButton({
  href,
  variant = 'primary',
  children,
  className = '',
  fullWidth = false,
  glow = true,
  ...props
}: PropsWithChildren<ButtonProps>) {
  const [isHovered, setIsHovered] = useState(false);

  const baseClasses =
    'relative font-bold py-3 px-8 rounded-md transition-all duration-300 inline-flex items-center justify-center focus:outline-none focus:ring-2 focus:ring-offset-2 overflow-hidden';

  const variantClasses = {
    primary: `
      bg-[#C1272D] text-white hover:bg-[#D1373D] focus:ring-[#C1272D]
      ${glow ? 'shadow-lg shadow-[#FFD700]/50 hover:shadow-[#FFD700]/70' : ''}
      transform hover:-translate-y-1
    `,
    secondary: `
      bg-[#FFD700] text-black hover:bg-[#FFDF20] focus:ring-[#FFD700]
      ${glow ? 'shadow-lg shadow-[#C1272D]/50 hover:shadow-[#C1272D]/70' : ''}
      transform hover:-translate-y-1
    `,
  };

  const widthClass = fullWidth ? 'w-full' : '';
  const buttonClasses = `${baseClasses} ${variantClasses[variant]} ${widthClass} ${className}`;

  // Add subtle particle effect on hover
  const renderParticles = () => {
    if (!isHovered) {return null;}

    return (
      <span className="absolute inset-0 pointer-events-none">
        {variant === 'primary' && (
          <span className="absolute top-0 left-1/2 w-1 h-1 rounded-full bg-[#FFD700] animate-ping"></span>
        )}
        {variant === 'secondary' && (
          <span className="absolute top-0 left-1/2 w-1 h-1 rounded-full bg-[#C1272D] animate-ping"></span>
        )}
      </span>
    );
  };

  if (href) {
    return (
      <Link
        href={href}
        className={buttonClasses}
        onMouseEnter={() => setIsHovered(true)}
        onMouseLeave={() => setIsHovered(false)}
        {...(props as any)} // Cast props to any to avoid TypeScript errors with event handlers
      >
        {renderParticles()}
        {children}
      </Link>
    );
  }

  return (
    <button
      className={buttonClasses}
      onMouseEnter={() => setIsHovered(true)}
      onMouseLeave={() => setIsHovered(false)}
      {...props}
    >
      {renderParticles()}
      {children}
    </button>
  );
}

'use client';
import { useEffect, useRef, useState } from 'react';
import { useJsApiLoader } from '@react-google-maps/api';

const libraries: 'places'[] = ['places'];

export type PlaceSuggestion = {
  formatted_address: string;
  place_id: string;
  lat: number;
  lng: number;
};

interface PlaceAutocompleteProps {
  onSelectAddress: (s: PlaceSuggestion) => void;
  placeholder?: string;
  className?: string;
  disabled?: boolean;
}

export default function PlaceAutocomplete({
  onSelectAddress,
  placeholder = 'Type an addressâ€¦',
  className = '',
  disabled = false,
}: PlaceAutocompleteProps) {
  const containerRef = useRef<HTMLDivElement>(null);
  const autocompleteElementRef = useRef<any>(null);
  const [loadError, setLoadError] = useState<string | null>(null);

  // Use the Google Maps API loader hook
  const { isLoaded, loadError: apiLoadError } = useJsApiLoader({
    id: 'google-map-script',
    googleMapsApiKey: process.env.NEXT_PUBLIC_GOOGLE_MAPS_API_KEY || '',
    libraries: libraries,
  });

  // Initialize PlaceAutocompleteElement when API is loaded
  useEffect(() => {
    if (!isLoaded || !containerRef.current || !window.google?.maps?.places?.PlaceAutocompleteElement) {
      return;
    }

    try {
      // Create the new PlaceAutocompleteElement
      const autocompleteElement = new window.google.maps.places.PlaceAutocompleteElement({
        types: ['address'],
      });

      // Configure the element
      autocompleteElement.placeholder = placeholder;
      autocompleteElement.disabled = disabled;

      // Apply custom styling
      autocompleteElement.style.setProperty('--gmp-input-border', '1px solid #ccc');
      autocompleteElement.style.setProperty('--gmp-input-border-radius', '0.375rem');
      autocompleteElement.style.setProperty('--gmp-input-padding', '0.5rem 1rem');
      autocompleteElement.style.width = '100%';

      // Store reference
      autocompleteElementRef.current = autocompleteElement;

      // Handle place selection
      const handlePlaceSelect = (event: any) => {
        const place = event.place;
        if (place && place.geometry && place.geometry.location) {
          const suggestion: PlaceSuggestion = {
            formatted_address: place.formatted_address || '',
            place_id: place.place_id || '',
            lat: place.geometry.location.lat(),
            lng: place.geometry.location.lng(),
          };
          onSelectAddress(suggestion);
          console.log('Selected place:', suggestion);
        }
      };

      // Add event listener
      autocompleteElement.addEventListener('gmp-placeselect', handlePlaceSelect);

      // Append to container
      containerRef.current.appendChild(autocompleteElement);

      // Cleanup function
      return () => {
        if (autocompleteElement) {
          autocompleteElement.removeEventListener('gmp-placeselect', handlePlaceSelect);
          if (autocompleteElement.parentNode) {
            autocompleteElement.parentNode.removeChild(autocompleteElement);
          }
        }
      };
    } catch (error) {
      console.error('Error initializing PlaceAutocompleteElement:', error);
      setLoadError('Failed to initialize address autocomplete');
    }
  }, [isLoaded, placeholder, disabled, onSelectAddress]);

  // Update placeholder when prop changes
  useEffect(() => {
    if (autocompleteElementRef.current) {
      autocompleteElementRef.current.placeholder = placeholder;
    }
  }, [placeholder]);

  // Update disabled state when prop changes
  useEffect(() => {
    if (autocompleteElementRef.current) {
      autocompleteElementRef.current.disabled = disabled;
    }
  }, [disabled]);

  // Handle loading state
  if (!isLoaded) {
    return (
      <div className={`w-full ${className}`}>
        <div className="flex items-center space-x-2 p-3 border border-gray-300 rounded-md bg-gray-50">
          <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-gray-900"></div>
          <span className="text-gray-600">Loading address search...</span>
        </div>
      </div>
    );
  }

  // Handle error state
  if (apiLoadError || loadError) {
    return (
      <div className={`w-full ${className}`}>
        <div className="p-3 border border-red-300 rounded-md bg-red-50">
          <span className="text-red-600">
            Error loading address search: {apiLoadError?.message || loadError}
          </span>
        </div>
      </div>
    );
  }

  return (
    <div className={`w-full ${className}`}>
      <div
        ref={containerRef}
        className="w-full"
        style={{
          minHeight: '40px',
        }}
      />
    </div>
  );
}

'use client';

import { useEffect, useState } from 'react';
import { Card } from '../ui/card';
import { Button } from '../ui/button';
import MenuAdmin from './MenuAdmin';

const TABS = [
  { key: 'menu', label: 'Menu' },
  { key: 'orders', label: 'Orders' },
  { key: 'rewards', label: 'Rewards' },
  { key: 'users', label: 'Users' },
  { key: 'analytics', label: 'Analytics' },
];

export default function AdminDashboard() {
  const [, setProfile] = useState<any>(null);
  const [loading, setLoading] = useState(true);
  const [tab, setTab] = useState('menu');
  const [error, setError] = useState<string | null>(null);

  useEffect(() => {
    async function fetchProfile() {
      setLoading(true);
      const token = await (window as any).firebase
        ?.auth()
        .currentUser?.getIdToken();
      const res = await fetch('/api/user-profile', {
        headers: { Authorization: `Bearer ${token}` },
      });
      const data = await res.json();
      if (data.user?.role !== 'admin') {
        setError('Access denied: Admins only');
      } else {
        setProfile(data);
      }
      setLoading(false);
    }
    fetchProfile();
  }, []);

  if (loading)
    {return <div className="text-center py-12">Loading admin dashboard...</div>;}
  if (error)
    {return <div className="text-center py-12 text-red-500">{error}</div>;}

  return (
    <div className="max-w-6xl mx-auto space-y-8">
      <Card className="p-6 flex flex-col gap-4">
        <h2 className="text-2xl font-bold mb-4">Admin Dashboard</h2>
        <div className="flex gap-2 mb-4">
          {TABS.map((t) => (
            <Button
              key={t.key}
              variant={tab === t.key ? 'default' : 'outline'}
              onClick={() => setTab(t.key)}
            >
              {t.label}
            </Button>
          ))}
        </div>
        <div className="mt-4">
          {tab === 'menu' && <MenuAdmin />}
          {tab === 'orders' && <div>Order Management (coming soon)</div>}
          {tab === 'rewards' && <div>Rewards Management (coming soon)</div>}
          {tab === 'users' && <div>User Management (coming soon)</div>}
          {tab === 'analytics' && <div>Analytics Overview (coming soon)</div>}
        </div>
      </Card>
    </div>
  );
}

'use client';

import React, { useEffect, useState } from 'react';
import { Card } from '../ui/card';
import { Button } from '../ui/button';
import { Input } from '../ui/input';
import { MenuItem } from '../../lib/firestoreModels';

export default function MenuAdmin() {
  const [menu, setMenu] = useState<MenuItem[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const [showAdd, setShowAdd] = useState(false);
  const [newItem, setNewItem] = useState<any>({
    name: '',
    price: '',
    description: '',
    type: 'classic',
    source: 'broskis',
  });
  const [saving, setSaving] = useState(false);
  const [editingId, setEditingId] = useState<string | null>(null);
  const [editItem, setEditItem] = useState<any>(null);
  const [deletingId, setDeletingId] = useState<string | null>(null);

  useEffect(() => {
    fetchMenu();
  }, []);

  async function fetchMenu() {
    setLoading(true);
    setError(null);
    try {
      const token = await (window as any).firebase
        ?.auth()
        .currentUser?.getIdToken();
      const res = await fetch('/api/admin/menu', {
        headers: { Authorization: `Bearer ${token}` },
      });
      const data = await res.json();
      setMenu(data.menu || []);
    } catch (e: any) {
      setError(e.message);
    } finally {
      setLoading(false);
    }
  }

  async function handleAdd() {
    setSaving(true);
    setError(null);
    try {
      const token = await (window as any).firebase
        ?.auth()
        .currentUser?.getIdToken();
      const res = await fetch('/api/admin/menu', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify({
          ...newItem,
          price: parseFloat(newItem.price),
        }),
      });
      const data = await res.json();
      if (data.error) {throw new Error(data.error);}
      setMenu((m) => [...m, data]);
      setShowAdd(false);
      setNewItem({
        name: '',
        price: '',
        description: '',
        type: 'classic',
        source: 'broskis',
      });
    } catch (e: any) {
      setError(e.message);
    } finally {
      setSaving(false);
    }
  }

  async function handleEdit(item: MenuItem) {
    setEditingId(item.id ?? null);
    setEditItem({ ...item });
  }

  async function handleEditSave() {
    if (!editItem) {return;}
    setSaving(true);
    setError(null);
    try {
      const token = await (window as any).firebase
        ?.auth()
        .currentUser?.getIdToken();
      const res = await fetch(`/api/admin/menu/${editingId}`, {
        method: 'PATCH',
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify({
          ...editItem,
          price: parseFloat(editItem.price),
        }),
      });
      const data = await res.json();
      if (data.error) {throw new Error(data.error);}
      setMenu((m) =>
        m.map((item) =>
          item.id === editingId ? { ...item, ...editItem } : item,
        ),
      );
      setEditingId(null);
      setEditItem(null);
    } catch (e: any) {
      setError(e.message);
    } finally {
      setSaving(false);
    }
  }

  async function handleDelete(id: string) {
    if (!window.confirm('Are you sure you want to delete this menu item?'))
      {return;}
    setDeletingId(id);
    setError(null);
    try {
      const token = await (window as any).firebase
        ?.auth()
        .currentUser?.getIdToken();
      const res = await fetch(`/api/admin/menu/${id}`, {
        method: 'DELETE',
        headers: { Authorization: `Bearer ${token}` },
      });
      const data = await res.json();
      if (data.error) {throw new Error(data.error);}
      setMenu((m) => m.filter((item) => item.id !== id));
    } catch (e: any) {
      setError(e.message);
    } finally {
      setDeletingId(null);
    }
  }

  return (
    <div>
      <div className="flex justify-between items-center mb-4">
        <h3 className="text-xl font-bold">Menu Management</h3>
        <Button onClick={() => setShowAdd((v) => !v)}>
          {showAdd ? 'Cancel' : 'Add New Item'}
        </Button>
      </div>
      {showAdd && (
        <Card className="p-4 mb-4">
          <div className="flex flex-col gap-2 md:flex-row md:gap-4">
            <Input
              placeholder="Name"
              value={newItem.name}
              onChange={(e) => setNewItem({ ...newItem, name: e.target.value })}
            />
            <Input
              placeholder="Price"
              type="number"
              value={newItem.price}
              onChange={(e) =>
                setNewItem({ ...newItem, price: e.target.value })
              }
            />
            <Input
              placeholder="Description"
              value={newItem.description}
              onChange={(e) =>
                setNewItem({ ...newItem, description: e.target.value })
              }
            />
            <select
              value={newItem.type}
              onChange={(e) => setNewItem({ ...newItem, type: e.target.value })}
              className="border rounded px-2 py-1"
            >
              <option value="classic">Classic</option>
              <option value="infused">Infused</option>
            </select>
            <select
              value={newItem.source}
              onChange={(e) =>
                setNewItem({ ...newItem, source: e.target.value })
              }
              className="border rounded px-2 py-1"
            >
              <option value="broskis">Broskis</option>
              <option value="partner">Partner</option>
            </select>
            <Button onClick={handleAdd} disabled={saving}>
              {saving ? 'Saving...' : 'Save'}
            </Button>
          </div>
        </Card>
      )}
      {loading ? (
        <div className="text-center py-8">Loading menu...</div>
      ) : error ? (
        <div className="text-red-500 text-center py-8">{error}</div>
      ) : (
        <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
          {menu.map((item, i) => (
            <Card key={item.id || i} className="p-4 flex flex-col gap-2">
              {editingId === item.id ? (
                <div className="flex flex-col gap-2">
                  <Input
                    value={editItem.name}
                    onChange={(e) =>
                      setEditItem({ ...editItem, name: e.target.value })
                    }
                  />
                  <Input
                    value={editItem.price}
                    type="number"
                    onChange={(e) =>
                      setEditItem({ ...editItem, price: e.target.value })
                    }
                  />
                  <Input
                    value={editItem.description}
                    onChange={(e) =>
                      setEditItem({ ...editItem, description: e.target.value })
                    }
                  />
                  <select
                    value={editItem.type}
                    onChange={(e) =>
                      setEditItem({ ...editItem, type: e.target.value })
                    }
                    className="border rounded px-2 py-1"
                  >
                    <option value="classic">Classic</option>
                    <option value="infused">Infused</option>
                  </select>
                  <select
                    value={editItem.source}
                    onChange={(e) =>
                      setEditItem({ ...editItem, source: e.target.value })
                    }
                    className="border rounded px-2 py-1"
                  >
                    <option value="broskis">Broskis</option>
                    <option value="partner">Partner</option>
                  </select>
                  <div className="flex gap-2">
                    <Button onClick={handleEditSave} disabled={saving}>
                      {saving ? 'Saving...' : 'Save'}
                    </Button>
                    <Button
                      variant="ghost"
                      onClick={() => {
                        setEditingId(null);
                        setEditItem(null);
                      }}
                    >
                      Cancel
                    </Button>
                  </div>
                </div>
              ) : (
                <>
                  <div className="flex justify-between items-center">
                    <div>
                      <div className="font-bold text-lg">{item.name}</div>
                      <div className="text-sm text-gray-400">
                        {item.type} | {item.source}
                      </div>
                    </div>
                    <div className="font-bold text-otw-gold-600">
                      ${item.price.toFixed(2)}
                    </div>
                  </div>
                  <div className="text-gray-300 text-sm mb-2">
                    {item.description}
                  </div>
                  <div className="flex gap-2">
                    <Button
                      size="sm"
                      variant="outline"
                      onClick={() => handleEdit(item)}
                    >
                      Edit
                    </Button>
                    <Button
                      size="sm"
                      variant="destructive"
                      onClick={() => handleDelete(item.id!)}
                      disabled={deletingId === item.id}
                    >
                      {deletingId === item.id ? 'Deleting...' : 'Delete'}
                    </Button>
                  </div>
                </>
              )}
            </Card>
          ))}
        </div>
      )}
    </div>
  );
}

'use client';

import { useState, useRef, useEffect } from 'react';
import Link from 'next/link';
import Image from 'next/image';
import { ShoppingBag, X, ChevronRight, Trash2 } from 'lucide-react';
import { useCart } from '../../lib/context/CartContext';
import { useOnClickOutside } from '../../hooks/useOnClickOutside';
import Button from '../Button';

export default function CartDropdown() {
  const [isOpen, setIsOpen] = useState(false);
  const { items, itemCount, removeItem, updateQuantity, total } = useCart();
  const dropdownRef = useRef<HTMLDivElement>(null);

  // Close dropdown when clicking outside
  useOnClickOutside(dropdownRef, () => setIsOpen(false));

  // Close dropdown when pressing escape key
  useEffect(() => {
    const handleEscape = (e: KeyboardEvent) => {
      if (e.key === 'Escape') {
        setIsOpen(false);
      }
    };

    document.addEventListener('keydown', handleEscape);
    return () => document.removeEventListener('keydown', handleEscape);
  }, []);

  return (
    <div className="relative" ref={dropdownRef}>
      <button
        className="flex items-center gap-1 px-4 py-2 text-white hover:text-otw-gold-600 transition-colors relative"
        onClick={() => setIsOpen(!isOpen)}
        aria-expanded={isOpen}
        aria-haspopup="true"
      >
        <ShoppingBag className="h-5 w-5" />
        {itemCount > 0 && (
          <span className="absolute -top-1 -right-1 bg-otw-red-600 text-white text-xs rounded-full h-5 w-5 flex items-center justify-center">
            {itemCount}
          </span>
        )}
      </button>

      {isOpen && (
        <div className="absolute right-0 mt-2 w-80 bg-otw-black-900 rounded-md shadow-otw-md z-50 border border-otw-black-800 overflow-hidden">
          <div className="p-4 border-b border-otw-black-800 flex justify-between items-center">
            <h3 className="font-medium">Your Cart ({itemCount})</h3>
            <button
              onClick={() => setIsOpen(false)}
              className="text-gray-400 hover:text-white"
              aria-label="Close cart"
            >
              <X className="h-4 w-4" />
            </button>
          </div>

          {items.length === 0 ? (
            <div className="p-6 text-center">
              <div className="flex justify-center mb-4">
                <ShoppingBag className="h-12 w-12 text-gray-500" />
              </div>
              <p className="text-gray-400 mb-4">Your cart is empty</p>
              <Link
                href="/restaurants"
                className="inline-block w-full"
                onClick={() => setIsOpen(false)}
              >
                <Button variant="primary" size="sm">
                  Browse Broski's Kitchen
                </Button>
              </Link>
            </div>
          ) : (
            <>
              <div className="max-h-80 overflow-y-auto p-2">
                {items.map((item) => (
                  <div
                    key={item.id}
                    className="flex items-center gap-3 p-2 hover:bg-otw-black-800 rounded-md transition-colors"
                  >
                    <div className="relative h-16 w-16 flex-shrink-0 rounded-md overflow-hidden">
                      <Image
                        src={item.image || '/placeholder.svg'}
                        alt={item.name}
                        fill
                        className="object-cover"
                      />
                    </div>
                    <div className="flex-grow min-w-0">
                      <h4 className="font-medium text-sm truncate">
                        {item.name}
                      </h4>
                      <p className="text-gray-400 text-xs">
                        ${item.price.toFixed(2)}
                      </p>
                      <div className="flex items-center mt-1">
                        <button
                          onClick={() =>
                            updateQuantity(
                              item.id,
                              Math.max(1, item.quantity - 1),
                            )
                          }
                          className="h-6 w-6 flex items-center justify-center bg-otw-black-800 rounded-md text-sm"
                          aria-label="Decrease quantity"
                        >
                          -
                        </button>
                        <span className="mx-2 text-sm">{item.quantity}</span>
                        <button
                          onClick={() =>
                            updateQuantity(item.id, item.quantity + 1)
                          }
                          className="h-6 w-6 flex items-center justify-center bg-otw-black-800 rounded-md text-sm"
                          aria-label="Increase quantity"
                        >
                          +
                        </button>
                      </div>
                    </div>
                    <button
                      onClick={() => removeItem(item.id)}
                      className="text-gray-400 hover:text-otw-red-600 transition-colors"
                      aria-label={`Remove ${item.name} from cart`}
                    >
                      <Trash2 className="h-4 w-4" />
                    </button>
                  </div>
                ))}
              </div>

              <div className="p-4 border-t border-otw-black-800">
                <div className="flex justify-between mb-4">
                  <span className="text-gray-400">Subtotal</span>
                  <span className="font-medium">${total.toFixed(2)}</span>
                </div>
                <Link
                  href="/checkout"
                  className="flex items-center justify-between w-full bg-otw-red-600 text-white py-2 px-4 rounded-md hover:bg-otw-red-700 transition-colors"
                  onClick={() => setIsOpen(false)}
                >
                  <span className="font-medium">Checkout</span>
                  <ChevronRight className="h-4 w-4" />
                </Link>
                <button
                  className="w-full text-center mt-2 text-sm text-gray-400 hover:text-white transition-colors"
                  onClick={() => setIsOpen(false)}
                >
                  Continue Shopping
                </button>
              </div>
            </>
          )}
        </div>
      )}
    </div>
  );
}

