'use client';

import type React from 'react';

import { useState } from 'react';
import {
  FaUser,
  FaEnvelope,
  FaPhone,
  FaComment,
  FaCheck,
} from 'react-icons/fa';

const ContactForm = () => {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    phone: '',
    subject: '',
    message: '',
  });

  const [errors, setErrors] = useState<Record<string, string>>({});
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [isSubmitted, setIsSubmitted] = useState(false);

  const handleChange = (
    e: React.ChangeEvent<
      HTMLInputElement | HTMLTextAreaElement | HTMLSelectElement
    >,
  ) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });

    // Clear error when field is updated
    if (errors[name]) {
      setErrors({
        ...errors,
        [name]: '',
      });
    }
  };

  const validateForm = () => {
    const newErrors: Record<string, string> = {};

    if (!formData.name.trim()) {
      newErrors.name = 'Name is required';
    }

    if (!formData.email.trim()) {
      newErrors.email = 'Email is required';
    } else if (!/\S+@\S+\.\S+/.test(formData.email)) {
      newErrors.email = 'Email is invalid';
    }

    if (!formData.message.trim()) {
      newErrors.message = 'Message is required';
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    if (!validateForm()) {
      return;
    }

    setIsSubmitting(true);

    try {
      // Simulate API call
      await new Promise((resolve) => setTimeout(resolve, 1500));

      // Show success message
      setIsSubmitted(true);

      // Reset form
      setFormData({
        name: '',
        email: '',
        phone: '',
        subject: '',
        message: '',
      });
    } catch (error) {
      console.error('Form submission error:', error);
      setErrors({
        ...errors,
        form: 'An error occurred. Please try again later.',
      });
    } finally {
      setIsSubmitting(false);
    }
  };

  const subjectOptions = [
    'General Inquiry',
    'Catering',
    'Events',
    'Delivery',
    'Feedback',
    'Career',
    'Other',
  ];

  return (
    <div className="bg-[#1A1A1A] rounded-lg p-6 border border-[#333333]">
      {isSubmitted ? (
        <div className="text-center py-8">
          <div className="w-16 h-16 bg-emerald-green bg-opacity-20 rounded-full flex items-center justify-center mx-auto mb-4">
            <FaCheck className="text-emerald-green text-2xl" />
          </div>
          <h3 className="text-xl font-bold mb-2">Message Sent!</h3>
          <p className="text-gray-300 mb-6">
            Thank you for reaching out. We'll get back to you as soon as
            possible.
          </p>
          <button className="btn-primary" onClick={() => setIsSubmitted(false)}>
            Send Another Message
          </button>
        </div>
      ) : (
        <form onSubmit={handleSubmit} className="space-y-6">
          {errors.form && (
            <div className="bg-blood-red bg-opacity-20 text-blood-red p-4 rounded-md">
              {errors.form}
            </div>
          )}

          <div>
            <label htmlFor="name" className="block text-sm font-medium mb-1">
              Name *
            </label>
            <div className="relative">
              <div className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none">
                <FaUser className="text-gray-500" />
              </div>
              <input
                type="text"
                id="name"
                name="name"
                value={formData.name}
                onChange={handleChange}
                className={`input pl-10 w-full ${errors.name ? 'border-blood-red' : ''}`}
                placeholder="Your name"
              />
            </div>
            {errors.name && (
              <p className="mt-1 text-sm text-blood-red">{errors.name}</p>
            )}
          </div>

          <div>
            <label htmlFor="email" className="block text-sm font-medium mb-1">
              Email *
            </label>
            <div className="relative">
              <div className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none">
                <FaEnvelope className="text-gray-500" />
              </div>
              <input
                type="email"
                id="email"
                name="email"
                value={formData.email}
                onChange={handleChange}
                className={`input pl-10 w-full ${errors.email ? 'border-blood-red' : ''}`}
                placeholder="your@email.com"
              />
            </div>
            {errors.email && (
              <p className="mt-1 text-sm text-blood-red">{errors.email}</p>
            )}
          </div>

          <div>
            <label htmlFor="phone" className="block text-sm font-medium mb-1">
              Phone (Optional)
            </label>
            <div className="relative">
              <div className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none">
                <FaPhone className="text-gray-500" />
              </div>
              <input
                type="tel"
                id="phone"
                name="phone"
                value={formData.phone}
                onChange={handleChange}
                className="input pl-10 w-full"
                placeholder="(123) 456-7890"
              />
            </div>
          </div>

          <div>
            <label htmlFor="subject" className="block text-sm font-medium mb-1">
              Subject
            </label>
            <select
              id="subject"
              name="subject"
              value={formData.subject}
              onChange={handleChange}
              className="input w-full"
            >
              <option value="">Select a subject</option>
              {subjectOptions.map((option) => (
                <option key={option} value={option}>
                  {option}
                </option>
              ))}
            </select>
          </div>

          <div>
            <label htmlFor="message" className="block text-sm font-medium mb-1">
              Message *
            </label>
            <div className="relative">
              <div className="absolute top-3 left-3 pointer-events-none">
                <FaComment className="text-gray-500" />
              </div>
              <textarea
                id="message"
                name="message"
                value={formData.message}
                onChange={handleChange}
                rows={5}
                className={`input pl-10 w-full ${errors.message ? 'border-blood-red' : ''}`}
                placeholder="How can we help you?"
              ></textarea>
            </div>
            {errors.message && (
              <p className="mt-1 text-sm text-blood-red">{errors.message}</p>
            )}
          </div>

          <button
            type="submit"
            className="btn-primary w-full"
            disabled={isSubmitting}
          >
            {isSubmitting ? (
              <span className="flex items-center justify-center">
                <svg
                  className="animate-spin -ml-1 mr-3 h-5 w-5 text-black"
                  xmlns="http://www.w3.org/2000/svg"
                  fill="none"
                  viewBox="0 0 24 24"
                >
                  <circle
                    className="opacity-25"
                    cx="12"
                    cy="12"
                    r="10"
                    stroke="currentColor"
                    strokeWidth="4"
                  ></circle>
                  <path
                    className="opacity-75"
                    fill="currentColor"
                    d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"
                  ></path>
                </svg>
                Sending...
              </span>
            ) : (
              'Send Message'
            )}
          </button>
        </form>
      )}
    </div>
  );
};

export default ContactForm;

'use client';

import type React from 'react';

import { useEffect, useRef, useState } from 'react';
import { useJsApiLoader } from '@react-google-maps/api';
import type { Location } from '../../types/location';

const libraries: 'places'[] = ['places'];

interface ContactMapProps {
  locations: Location[];
}

const ContactMap: React.FC<ContactMapProps> = ({ locations }) => {
  const mapRef = useRef<HTMLDivElement>(null);
  const [selectedLocation, setSelectedLocation] = useState<Location | null>(
    null,
  );

  // Use the Google Maps API loader hook
  const { isLoaded, loadError } = useJsApiLoader({
    id: 'google-map-script',
    googleMapsApiKey: process.env.NEXT_PUBLIC_GOOGLE_MAPS_API_KEY || '',
    libraries: libraries,
  });

  // Calculate center point of all locations
  const calculateCenter = () => {
    if (locations.length === 0) {return { lat: 37.0902, lng: -95.7129 };} // Default to US center

    const totalLat = locations.reduce(
      (sum, loc) => sum + loc.coordinates.lat,
      0,
    );
    const totalLng = locations.reduce(
      (sum, loc) => sum + loc.coordinates.lng,
      0,
    );

    return {
      lat: totalLat / locations.length,
      lng: totalLng / locations.length,
    };
  };

  useEffect(() => {
    if (!isLoaded || !mapRef.current) {return;}

    // Initialize map
    const initializeMap = () => {
      const center = calculateCenter();

      // Create map instance
      const mapOptions = {
        center: center,
        zoom: 5,
        styles: [
          { elementType: 'geometry', stylers: [{ color: '#242f3e' }] },
          {
            elementType: 'labels.text.stroke',
            stylers: [{ color: '#242f3e' }],
          },
          { elementType: 'labels.text.fill', stylers: [{ color: '#746855' }] },
          {
            featureType: 'administrative.locality',
            elementType: 'labels.text.fill',
            stylers: [{ color: '#d59563' }],
          },
          {
            featureType: 'poi',
            elementType: 'labels.text.fill',
            stylers: [{ color: '#d59563' }],
          },
          {
            featureType: 'poi.park',
            elementType: 'geometry',
            stylers: [{ color: '#263c3f' }],
          },
          {
            featureType: 'poi.park',
            elementType: 'labels.text.fill',
            stylers: [{ color: '#6b9a76' }],
          },
          {
            featureType: 'road',
            elementType: 'geometry',
            stylers: [{ color: '#38414e' }],
          },
          {
            featureType: 'road',
            elementType: 'geometry.stroke',
            stylers: [{ color: '#212a37' }],
          },
          {
            featureType: 'road',
            elementType: 'labels.text.fill',
            stylers: [{ color: '#9ca5b3' }],
          },
          {
            featureType: 'road.highway',
            elementType: 'geometry',
            stylers: [{ color: '#746855' }],
          },
          {
            featureType: 'road.highway',
            elementType: 'geometry.stroke',
            stylers: [{ color: '#1f2835' }],
          },
          {
            featureType: 'road.highway',
            elementType: 'labels.text.fill',
            stylers: [{ color: '#f3d19c' }],
          },
          {
            featureType: 'transit',
            elementType: 'geometry',
            stylers: [{ color: '#2f3948' }],
          },
          {
            featureType: 'transit.station',
            elementType: 'labels.text.fill',
            stylers: [{ color: '#d59563' }],
          },
          {
            featureType: 'water',
            elementType: 'geometry',
            stylers: [{ color: '#17263c' }],
          },
          {
            featureType: 'water',
            elementType: 'labels.text.fill',
            stylers: [{ color: '#515c6d' }],
          },
          {
            featureType: 'water',
            elementType: 'labels.text.stroke',
            stylers: [{ color: '#17263c' }],
          },
        ],
        disableDefaultUI: true,
        zoomControl: true,
        mapTypeControl: false,
        scaleControl: true,
        streetViewControl: false,
        rotateControl: false,
        fullscreenControl: true,
      };

      const map = new window.google.maps.Map(mapRef.current, mapOptions);

      // Add markers for each location
      locations.forEach((location) => {
        const marker = new window.google.maps.Marker({
          position: {
            lat: location.coordinates.lat,
            lng: location.coordinates.lng,
          },
          map: map,
          title: location.name,
          icon: {
            url: '/images/marker.png',
            scaledSize: new window.google.maps.Size(30, 40),
          },
        });

        // Create info window
        const infoWindow = new window.google.maps.InfoWindow({
          content: `
            <div style="color: #000; padding: 5px;">
              <h3 style="font-weight: bold; margin-bottom: 5px;">${location.name}</h3>
              <p style="margin: 0;">${location.address}, ${location.city}, ${location.state}</p>
              <p style="margin: 5px 0 0;"><a href="tel:${location.phone}" style="color: #D4AF37;">${location.phone}</a></p>
            </div>
          `,
        });

        // Add click event to marker
        marker.addListener('click', () => {
          // Close any open info windows
          infoWindow.close();

          // Open this info window
          infoWindow.open(map, marker);

          // Set selected location
          setSelectedLocation(location);
        });
      });
    };

    initializeMap();
  }, [isLoaded, locations, setSelectedLocation, calculateCenter]);

  if (loadError) {
    return (
      <div className="w-full h-full flex items-center justify-center text-red-500">
        Error loading Google Maps
      </div>
    );
  }

  if (!isLoaded) {
    return (
      <div className="w-full h-full flex items-center justify-center">
        Loading map...
      </div>
    );
  }

  return (
    <div className="w-full h-full">
      <div ref={mapRef} className="w-full h-full rounded-lg" />
      <div className="sr-only" aria-live="polite">
        {selectedLocation
          ? `Selected location: ${selectedLocation.name} at ${selectedLocation.address}, ${selectedLocation.city}, ${selectedLocation.state}`
          : "Map showing all Broski's Kitchen locations"}
      </div>
    </div>
  );
};

export default ContactMap;

'use client';

import { useState, useEffect } from 'react';
import { Button } from './ui/button';
import { X } from 'lucide-react';

export function CookieConsent() {
  const [isVisible, setIsVisible] = useState(false);

  useEffect(() => {
    // Check if user has already consented
    const hasConsented = localStorage.getItem('cookie-consent');
    if (!hasConsented) {
      // Show banner after a short delay
      const timer = setTimeout(() => {
        setIsVisible(true);
      }, 1000);
      return () => clearTimeout(timer);
    }
  }, []);

  const acceptAll = () => {
    localStorage.setItem('cookie-consent', 'all');
    setIsVisible(false);
  };

  const acceptEssential = () => {
    localStorage.setItem('cookie-consent', 'essential');
    setIsVisible(false);
  };

  if (!isVisible) {return null;}

  return (
    <div className="fixed bottom-0 left-0 right-0 z-50 bg-otw-black-900 border-t border-otw-black-800 p-4 md:p-6 shadow-lg">
      <div className="container mx-auto max-w-7xl">
        <div className="flex flex-col md:flex-row items-start md:items-center justify-between gap-4">
          <div className="flex-1">
            <h3 className="text-lg font-semibold mb-2">
              We value your privacy
            </h3>
            <p className="text-sm text-gray-400 mb-2">
              We use cookies to enhance your browsing experience, serve
              personalized ads or content, and analyze our traffic. By clicking
              "Accept All", you consent to our use of cookies.
            </p>
            <a
              href="/privacy-policy"
              className="text-sm text-otw-gold-600 hover:underline"
            >
              Read our Cookie Policy
            </a>
          </div>

          <div className="flex flex-col sm:flex-row gap-2 sm:items-center">
            <Button
              variant="outline"
              size="sm"
              onClick={acceptEssential}
              className="whitespace-nowrap"
            >
              Essential Only
            </Button>
            <Button
              variant="default"
              size="sm"
              onClick={acceptAll}
              className="whitespace-nowrap bg-otw-gold-600 hover:bg-otw-gold-700"
            >
              Accept All
            </Button>
            <Button
              variant="ghost"
              size="icon"
              onClick={acceptEssential}
              className="absolute top-2 right-2 md:hidden"
            >
              <X className="h-4 w-4" />
              <span className="sr-only">Close</span>
            </Button>
          </div>
        </div>
      </div>
    </div>
  );
}

'use client';

import { useState, useEffect } from 'react';
import { Card } from '../ui/card';
import { Button } from '../ui/button';
import { Clock, MapPin, DollarSign, Star, Loader2 } from 'lucide-react';
import { useAuth } from '../../contexts/AuthContext';
import { collection, query, where, orderBy, getDocs, limit } from 'firebase/firestore';
import { db } from '../../lib/firebase-config';

interface Order {
  id: string;
  userRef: string;
  total: number;
  status: string;
  createdAt: any;
  cart?: any[];
  stripeId?: string;
}

interface ScreenshotOrder {
  id: string;
  orderId: string;
  status: string;
  customerInfo: {
    name: string;
    phone: string;
    email: string;
  };
  restaurantInfo: {
    name: string;
    pickupLocation: string;
  };
  orderDetails: {
    estimatedTotal: string;
    specialInstructions: string;
  };
  timestamps: {
    created: string;
    updated?: string;
  };
}

export default function OrderHistory() {
  const [orders, setOrders] = useState<Order[]>([]);
  const [screenshotOrders, setScreenshotOrders] = useState<ScreenshotOrder[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const { user } = useAuth();

  useEffect(() => {
    const fetchOrders = async () => {
      if (!user?.uid) {
        setLoading(false);
        return;
      }

      try {
        setLoading(true);
        setError(null);

        // Fetch regular orders
        const ordersQuery = query(
          collection(db, 'orders'),
          where('userRef', '==', user.uid),
          orderBy('createdAt', 'desc'),
          limit(20),
        );
        const ordersSnapshot = await getDocs(ordersQuery);
        const ordersData = ordersSnapshot.docs.map(doc => ({
          id: doc.id,
          ...doc.data(),
        })) as Order[];

        // Fetch screenshot orders
        const screenshotOrdersQuery = query(
          collection(db, 'screenshot_orders'),
          where('customerInfo.email', '==', user.email || ''),
          orderBy('timestamps.created', 'desc'),
          limit(20),
        );
        const screenshotOrdersSnapshot = await getDocs(screenshotOrdersQuery);
        const screenshotOrdersData = screenshotOrdersSnapshot.docs.map(doc => ({
          id: doc.id,
          ...doc.data(),
        })) as ScreenshotOrder[];

        setOrders(ordersData);
        setScreenshotOrders(screenshotOrdersData);
      } catch (err) {
        console.error('Error fetching orders:', err);
        setError('Failed to load order history');
      } finally {
        setLoading(false);
      }
    };

    fetchOrders();
  }, [user]);

  // Calculate statistics
  const totalOrders = orders.length + screenshotOrders.length;
  const totalSpent = orders.reduce((sum, order) => sum + (order.total || 0), 0) +
    screenshotOrders.reduce((sum, order) => {
      const total = parseFloat(order.orderDetails?.estimatedTotal?.replace(/[^\d.]/g, '') || '0');
      return sum + total;
    }, 0);

  // For now, we'll show a placeholder for average rating since we don't have rating data
  const avgRating = 'N/A';

  // Format date helper
  const formatDate = (date: any) => {
    if (!date) {return 'Unknown';}

    let dateObj: Date;
    if (typeof date === 'string') {
      dateObj = new Date(date);
    } else if (date.toDate && typeof date.toDate === 'function') {
      dateObj = date.toDate();
    } else if (date instanceof Date) {
      dateObj = date;
    } else {
      return 'Unknown';
    }

    return dateObj.toLocaleDateString('en-US', {
      year: 'numeric',
      month: 'short',
      day: 'numeric',
    });
  };

  // Get status color
  const getStatusColor = (status: string) => {
    switch (status.toLowerCase()) {
      case 'completed':
      case 'fulfilled':
      case 'delivered':
        return 'text-green-500';
      case 'pending':
      case 'pending_review':
      case 'confirmed':
        return 'text-yellow-500';
      case 'cancelled':
      case 'failed':
        return 'text-red-500';
      default:
        return 'text-gray-400';
    }
  };

  if (loading) {
    return (
      <div className="space-y-6">
        <div className="flex items-center justify-center py-12">
          <Loader2 className="h-8 w-8 animate-spin text-otw-gold" />
          <span className="ml-2 text-lg">Loading order history...</span>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="space-y-6">
        <Card className="p-6 bg-red-900/20 border-red-500">
          <p className="text-red-400">{error}</p>
        </Card>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
        <Card className="p-6 bg-gray-900">
          <h3 className="text-lg font-semibold text-otw-gold mb-2">
            Total Orders
          </h3>
          <p className="text-3xl font-bold">{totalOrders}</p>
        </Card>
        <Card className="p-6 bg-gray-900">
          <h3 className="text-lg font-semibold text-otw-gold mb-2">
            Total Spent
          </h3>
          <p className="text-3xl font-bold">${totalSpent.toFixed(2)}</p>
        </Card>
        <Card className="p-6 bg-gray-900">
          <h3 className="text-lg font-semibold text-otw-gold mb-2">
            Avg Rating
          </h3>
          <p className="text-3xl font-bold">{avgRating}</p>
        </Card>
      </div>

      <div className="space-y-4">
        {totalOrders === 0 ? (
          <Card className="p-6">
            <div className="text-center py-8">
              <p className="text-lg text-gray-400 mb-4">No orders found</p>
              <p className="text-sm text-gray-500">Your order history will appear here once you place your first order.</p>
            </div>
          </Card>
        ) : (
          <>
            {/* Regular Orders */}
            {orders.map((order) => (
              <Card key={`order-${order.id}`} className="p-6">
                <div className="flex flex-col md:flex-row justify-between gap-4">
                  <div className="space-y-2">
                    <div className="flex items-center gap-2">
                      <h3 className="font-semibold text-lg">Food Order</h3>
                      <span className="text-sm text-gray-400">#{order.id.slice(-8)}</span>
                    </div>
                    <div className="flex items-center gap-4 text-sm text-gray-400">
                      <div className="flex items-center gap-1">
                        <Clock className="w-4 h-4" />
                        {formatDate(order.createdAt)}
                      </div>
                      <div className="flex items-center gap-1">
                        <MapPin className="w-4 h-4" />
                        Fort Wayne
                      </div>
                    </div>
                  </div>

                  <div className="flex flex-col md:flex-row items-start md:items-center gap-4">
                    <div className="flex items-center gap-2">
                      <DollarSign className="w-4 h-4 text-otw-gold" />
                      <span className="font-semibold">${order.total?.toFixed(2) || '0.00'}</span>
                    </div>
                    <div className={`capitalize ${getStatusColor(order.status)}`}>
                      {order.status}
                    </div>
                    <Button variant="outline" size="sm">
                      View Details
                    </Button>
                  </div>
                </div>
              </Card>
            ))}

            {/* Screenshot Orders */}
            {screenshotOrders.map((order) => (
              <Card key={`screenshot-${order.id}`} className="p-6">
                <div className="flex flex-col md:flex-row justify-between gap-4">
                  <div className="space-y-2">
                    <div className="flex items-center gap-2">
                      <h3 className="font-semibold text-lg">Screenshot Order - {order.restaurantInfo?.name || 'Restaurant'}</h3>
                      <span className="text-sm text-gray-400">#{order.orderId}</span>
                    </div>
                    <div className="flex items-center gap-4 text-sm text-gray-400">
                      <div className="flex items-center gap-1">
                        <Clock className="w-4 h-4" />
                        {formatDate(order.timestamps?.created)}
                      </div>
                      <div className="flex items-center gap-1">
                        <MapPin className="w-4 h-4" />
                        {order.restaurantInfo?.pickupLocation || 'Fort Wayne'}
                      </div>
                    </div>
