    </div>
  );
}

/**
 * Current location tab component
 */
function CurrentLocationTab() {
  const {
    coords,
    address,
    loading,
    error,
    getCurrentLocation,
    clearLocation,
    hasLocation,
  } = useCurrentLocation();

  const [targetLat, setTargetLat] = useState('');
  const [targetLng, setTargetLng] = useState('');
  const [distance, setDistance] = useState<number | null>(null);

  const calculateDistanceToTarget = () => {
    if (coords && targetLat && targetLng) {
      const targetLatNum = parseFloat(targetLat);
      const targetLngNum = parseFloat(targetLng);

      if (isValidCoordinates(targetLatNum, targetLngNum)) {
        const dist = calculateDistance(
          coords.latitude,
          coords.longitude,
          targetLatNum,
          targetLngNum,
        );
        setDistance(dist);
      }
    }
  };

  return (
    <div className="space-y-4">
      <h2 className="text-xl font-semibold text-gray-900">Current Location</h2>
      <p className="text-gray-600">Get your current location and reverse geocode it to an address.</p>

      <div className="flex space-x-2">
        <button
          onClick={getCurrentLocation}
          disabled={loading}
          className="px-4 py-2 bg-blue-600 text-white rounded-md hover:bg-blue-700 disabled:opacity-50 disabled:cursor-not-allowed"
        >
          {loading ? 'Getting Location...' : 'Get Current Location'}
        </button>

        {hasLocation && (
          <button
            onClick={clearLocation}
            className="px-4 py-2 bg-gray-600 text-white rounded-md hover:bg-gray-700"
          >
            Clear Location
          </button>
        )}
      </div>

      {error && (
        <div className="p-3 bg-red-50 border border-red-200 rounded-md">
          <p className="text-red-700">{error}</p>
        </div>
      )}

      {coords && (
        <div className="space-y-4">
          <div className="p-4 bg-green-50 border border-green-200 rounded-md">
            <h3 className="font-semibold text-green-800 mb-2">Current Coordinates</h3>
            <p className="text-sm text-green-700">
              Latitude: {coords.latitude.toFixed(6)}<br />
              Longitude: {coords.longitude.toFixed(6)}
            </p>
          </div>

          {address && (
            <div className="p-4 bg-blue-50 border border-blue-200 rounded-md">
              <h3 className="font-semibold text-blue-800 mb-2">Current Address</h3>
              <p className="text-sm text-blue-700">{address.formatted_address}</p>
            </div>
          )}

          {/* Distance Calculator */}
          <div className="p-4 bg-gray-50 border border-gray-200 rounded-md">
            <h3 className="font-semibold text-gray-800 mb-2">Distance Calculator</h3>
            <p className="text-sm text-gray-600 mb-3">Calculate distance to another location:</p>

            <div className="grid grid-cols-1 md:grid-cols-3 gap-2">
              <input
                type="number"
                value={targetLat}
                onChange={(e) => setTargetLat(e.target.value)}
                placeholder="Target Latitude"
                step="any"
                className="px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
              />
              <input
                type="number"
                value={targetLng}
                onChange={(e) => setTargetLng(e.target.value)}
                placeholder="Target Longitude"
                step="any"
                className="px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
              />
              <button
                onClick={calculateDistanceToTarget}
                disabled={!targetLat || !targetLng}
                className="px-4 py-2 bg-blue-600 text-white rounded-md hover:bg-blue-700 disabled:opacity-50 disabled:cursor-not-allowed"
              >
                Calculate
              </button>
            </div>

            {distance !== null && (
              <div className="mt-3 p-3 bg-white border border-gray-200 rounded-md">
                <p className="text-sm">
                  <strong>Distance:</strong> {distance.toFixed(2)} km ({(distance * 0.621371).toFixed(2)} miles)
                </p>
              </div>
            )}
          </div>
        </div>
      )}
    </div>
  );
}

'use client';

import React, { useState, useEffect, useCallback } from 'react';
import { Search, MapPin, Navigation, Calculator } from 'lucide-react';
import {
  useLoadScript,
  GoogleMap,
  Marker,
} from '@react-google-maps/api';
import { Button } from '../ui/button';
import { Card, CardContent, CardHeader, CardTitle } from '../ui/card';
import { Label } from '../ui/label';
import { Separator } from '../ui/separator';
import PlaceAutocomplete from './PlaceAutocomplete';

const libraries: 'places'[] = ['places'];

interface AddressSearchProps {
  onAddressSelect?: (address: {
    formatted_address: string;
    lat: number;
    lng: number;
    place_id?: string;
  }) => void;
  onDistanceCalculated?: (data: {
    distance: number;
    duration: number;
    fee: number;
  }) => void;
  defaultLocation?: { lat: number; lng: number };
  height?: string;
  showFeeCalculator?: boolean;
  baseFee?: number;
  perMileRate?: number;
}

interface RouteInfo {
  distance: {
    text: string;
    value: number;
  };
  duration: {
    text: string;
    value: number;
  };
  fee: number;
}

export default function AddressSearch({
  onAddressSelect,
  onDistanceCalculated,
  defaultLocation = { lat: 40.7128, lng: -74.006 },
  height = '500px',
  showFeeCalculator = true,
  baseFee = 5.99,
  perMileRate = 1.5,
}: AddressSearchProps) {
  const [map, setMap] = useState<google.maps.Map | null>(null);
  const [directionsService, setDirectionsService] =
    useState<google.maps.DirectionsService | null>(null);
  const [directionsRenderer, setDirectionsRenderer] =
    useState<google.maps.DirectionsRenderer | null>(null);

  const [originLocation, setOriginLocation] = useState<google.maps.LatLng | null>(null);
  const [destinationLocation, setDestinationLocation] = useState<google.maps.LatLng | null>(null);
  const [currentLocation, setCurrentLocation] = useState<google.maps.LatLng | null>(null);
  const [routeInfo, setRouteInfo] = useState<RouteInfo | null>(null);
  const [isCalculating, setIsCalculating] = useState(false);



  const { isLoaded, loadError } = useLoadScript({
    googleMapsApiKey: process.env.NEXT_PUBLIC_GOOGLE_MAPS_API_KEY || '',
    libraries,
  });

  // Get user's current location
  useEffect(() => {
    if (navigator.geolocation && isLoaded) {
      navigator.geolocation.getCurrentPosition(
        (position) => {
          const { latitude, longitude } = position.coords;
          const location = new google.maps.LatLng(latitude, longitude);
          setCurrentLocation(location);
          if (map) {
            map.panTo(location);
          }
        },
        (error) => {
          console.error('Error getting location:', error);
        },
        {
          enableHighAccuracy: true,
          timeout: 10000,
          maximumAge: 300000, // 5 minutes
        },
      );
    }
  }, [map, isLoaded]);

  const onMapLoad = useCallback((map: google.maps.Map) => {
    setMap(map);
    const directionsService = new google.maps.DirectionsService();
    const directionsRenderer = new google.maps.DirectionsRenderer({
      suppressMarkers: false,
      draggable: true,
    });
    directionsRenderer.setMap(map);
    setDirectionsService(directionsService);
    setDirectionsRenderer(directionsRenderer);
  }, []);



  const calculateFee = useCallback((distanceInMeters: number): number => {
    const distanceInMiles = distanceInMeters / 1609.34;
    return Math.round((baseFee + (distanceInMiles * perMileRate)) * 100) / 100;
  }, [baseFee, perMileRate]);

  const calculateRoute = useCallback(async () => {
    if (!directionsService || !originLocation || !destinationLocation) {
      return;
    }

    setIsCalculating(true);

    try {
      const result = await new Promise<google.maps.DirectionsResult>((resolve, reject) => {
        directionsService.route(
          {
            origin: originLocation,
            destination: destinationLocation,
            travelMode: google.maps.TravelMode.DRIVING,
            unitSystem: google.maps.UnitSystem.IMPERIAL,
            avoidHighways: false,
            avoidTolls: false,
          },
          (result, status) => {
            if (status === google.maps.DirectionsStatus.OK && result) {
              resolve(result);
            } else {
              reject(new Error(`Directions request failed: ${status}`));
            }
          },
        );
      });

      if (directionsRenderer) {
        directionsRenderer.setDirections(result);
      }

      const route = result.routes[0];
      if (!route) {return;}

      const leg = route.legs[0];
      if (!leg) {return;}

      const distance = leg.distance?.value || 0;
      const duration = leg.duration?.value || 0;
      const fee = calculateFee(distance);

      const routeData: RouteInfo = {
        distance: {
          text: leg.distance?.text || '0 mi',
          value: distance,
        },
        duration: {
          text: leg.duration?.text || '0 mins',
          value: duration,
        },
        fee,
      };

      setRouteInfo(routeData);

      if (onDistanceCalculated) {
        onDistanceCalculated({
          distance,
          duration,
          fee,
        });
      }
    } catch (error) {
      console.error('Error calculating route:', error);
    } finally {
      setIsCalculating(false);
    }
  }, [directionsService, directionsRenderer, originLocation, destinationLocation, calculateFee, onDistanceCalculated]);

  const onOriginPlaceSelect = useCallback((place: any) => {
    if (place && place.geometry && place.geometry.location) {
      const location = new google.maps.LatLng(
        place.geometry.location.lat(),
        place.geometry.location.lng(),
      );
      setOriginLocation(location);

      if (onAddressSelect) {
        const addressData: any = {
          formatted_address: place.formatted_address || '',
          lat: place.geometry.location.lat(),
          lng: place.geometry.location.lng(),
        };
        if (place.place_id) {
          addressData.place_id = place.place_id;
        }
        onAddressSelect(addressData);
      }

      if (map) {
        map.panTo(location);
      }
    }
  }, [onAddressSelect, map]);

  const onDestinationPlaceSelect = useCallback((place: any) => {
    if (place && place.geometry && place.geometry.location) {
      const location = new google.maps.LatLng(
        place.geometry.location.lat(),
        place.geometry.location.lng(),
      );
      setDestinationLocation(location);

      if (map) {
        const bounds = new google.maps.LatLngBounds();
        if (originLocation) {bounds.extend(originLocation);}
        bounds.extend(location);
        map.fitBounds(bounds);
      }
    }
  }, [map, originLocation]);

  const handleCurrentLocation = useCallback(() => {
    if (currentLocation && map) {
      map.panTo(currentLocation);
      map.setZoom(15);
      setOriginLocation(currentLocation);

      // Note: PlaceAutocomplete will handle the display value internally

      if (onAddressSelect) {
        onAddressSelect({
          formatted_address: 'Current Location',
          lat: currentLocation.lat(),
          lng: currentLocation.lng(),
        });
      }
    }
  }, [currentLocation, map, onAddressSelect]);

  const clearRoute = useCallback(() => {
    if (directionsRenderer) {
      directionsRenderer.setDirections({ routes: [] } as any);
    }
    setRouteInfo(null);
    setOriginLocation(null);
    setDestinationLocation(null);
    // Note: PlaceAutocomplete components will handle clearing internally
  }, [directionsRenderer]);

  // Auto-calculate route when both locations are set
  useEffect(() => {
    if (originLocation && destinationLocation) {
      calculateRoute();
    }
  }, [originLocation, destinationLocation, calculateRoute]);

  if (loadError) {
    return (
      <Card className="w-full">
        <CardContent className="p-6">
          <div className="text-center text-red-500">
            Error loading Google Maps. Please check your API key configuration.
          </div>
        </CardContent>
      </Card>
    );
  }

  if (!isLoaded) {
    return (
      <Card className="w-full">
        <CardContent className="p-6">
          <div className="text-center">Loading maps...</div>
        </CardContent>
      </Card>
    );
  }

  return (
    <Card className="w-full overflow-hidden">
      <CardHeader>
        <CardTitle className="flex items-center gap-2">
          <MapPin className="h-5 w-5" />
          Address Search & Route Calculator
        </CardTitle>
      </CardHeader>

      <CardContent className="space-y-4">
        {/* Origin Address Input */}
        <div className="space-y-2">
          <Label htmlFor="origin">From (Origin)</Label>
          <div className="relative">
            <PlaceAutocomplete
              onPlaceSelect={onOriginPlaceSelect}
              placeholder="Enter pickup address..."
              className="pl-10"
              types={['address']}
              componentRestrictions={{ country: 'us' }}
            />
            <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400 h-4 w-4" />
            <Button
              type="button"
              variant="ghost"
              size="sm"
              className="absolute right-2 top-1/2 transform -translate-y-1/2"
              onClick={handleCurrentLocation}
            >
              <Navigation className="h-4 w-4" />
            </Button>
          </div>
        </div>

        {/* Destination Address Input */}
        <div className="space-y-2">
          <Label htmlFor="destination">To (Destination)</Label>
          <div className="relative">
            <PlaceAutocomplete
              onPlaceSelect={onDestinationPlaceSelect}
              placeholder="Enter delivery address..."
              className="pl-10"
              types={['address']}
              componentRestrictions={{ country: 'us' }}
            />
            <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400 h-4 w-4" />
          </div>
        </div>

        {/* Action Buttons */}
        <div className="flex gap-2">
          <Button
            onClick={calculateRoute}
            disabled={!originLocation || !destinationLocation || isCalculating}
            className="flex-1"
          >
            {isCalculating ? (
              <>
                <Calculator className="mr-2 h-4 w-4 animate-spin" />
                Calculating...
              </>
            ) : (
              <>
                <Calculator className="mr-2 h-4 w-4" />
                Calculate Route
              </>
            )}
          </Button>
          <Button variant="outline" onClick={clearRoute}>
            Clear
          </Button>
        </div>

        {/* Route Information */}
        {routeInfo && showFeeCalculator && (
          <div className="space-y-3 p-4 bg-gray-50 rounded-lg">
            <h4 className="font-semibold text-sm">Route Information</h4>
            <div className="grid grid-cols-3 gap-4 text-sm">
              <div className="text-center">
                <div className="font-medium text-blue-600">{routeInfo.distance.text}</div>
                <div className="text-gray-500">Distance</div>
              </div>
              <div className="text-center">
                <div className="font-medium text-green-600">{routeInfo.duration.text}</div>
                <div className="text-gray-500">Duration</div>
              </div>
              <div className="text-center">
                <div className="font-medium text-purple-600">${routeInfo.fee.toFixed(2)}</div>
                <div className="text-gray-500">Delivery Fee</div>
              </div>
            </div>
            <Separator />
            <div className="text-xs text-gray-600">
              <div>Base fee: ${baseFee.toFixed(2)}</div>
              <div>Rate: ${perMileRate.toFixed(2)} per mile</div>
            </div>
          </div>
        )}

        {/* Map */}
        <div className="relative rounded-lg overflow-hidden" style={{ height }}>
          <GoogleMap
            mapContainerStyle={{ width: '100%', height: '100%' }}
            center={currentLocation || defaultLocation}
            zoom={13}
            onLoad={onMapLoad}
            options={{
              styles: [
                {
                  featureType: 'poi',
                  elementType: 'labels',
                  stylers: [{ visibility: 'off' }],
                },
              ],
              disableDefaultUI: true,
              zoomControl: true,
              mapTypeControl: false,
              streetViewControl: false,
              fullscreenControl: true,
            }}
          >
            {/* Origin Marker */}
            {originLocation && (
              <Marker
                position={originLocation}
                title="Origin"
                icon={{
                  url: "data:image/svg+xml;charset=UTF-8,%3csvg width='20' height='20' viewBox='0 0 20 20' fill='none' xmlns='http://www.w3.org/2000/svg'%3e%3ccircle cx='10' cy='10' r='8' fill='%234ade80'/%3e%3ccircle cx='10' cy='10' r='3' fill='white'/%3e%3c/svg%3e",
                  scaledSize: new google.maps.Size(20, 20),
                }}
              />
            )}

            {/* Destination Marker */}
            {destinationLocation && (
              <Marker
                position={destinationLocation}
                title="Destination"
                icon={{
                  url: "data:image/svg+xml;charset=UTF-8,%3csvg width='20' height='20' viewBox='0 0 20 20' fill='none' xmlns='http://www.w3.org/2000/svg'%3e%3ccircle cx='10' cy='10' r='8' fill='%23ef4444'/%3e%3ccircle cx='10' cy='10' r='3' fill='white'/%3e%3c/svg%3e",
                  scaledSize: new google.maps.Size(20, 20),
                }}
              />
            )}
          </GoogleMap>
        </div>
      </CardContent>
    </Card>
  );
}

'use client';

import React, { useState, useCallback, useEffect } from 'react';
import {
  GoogleMap,
  Marker,
  useLoadScript,
} from '@react-google-maps/api';
import { Card } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Search, Navigation } from 'lucide-react';
import PlaceAutocomplete from './PlaceAutocomplete';

const libraries: 'places'[] = ['places'];

interface MapSearchProps {
  onLocationSelect?: (location: {
    lat: number;
    lng: number;
    address: string;
  }) => void;
  defaultLocation?: { lat: number; lng: number };
  height?: string;
  showSearchBar?: boolean;
}

export default function MapSearch({
  onLocationSelect,
  defaultLocation = { lat: 41.0793, lng: -85.1394 }, // Default to Fort Wayne, IN
  height = '400px',
  showSearchBar = true,
}: MapSearchProps) {
  const [map, setMap] = useState<google.maps.Map | null>(null);
  const [selectedLocation, setSelectedLocation] =
    useState<google.maps.LatLng | null>(null);
  const [currentLocation, setCurrentLocation] =
    useState<google.maps.LatLng | null>(null);


  const { isLoaded, loadError } = useLoadScript({
    googleMapsApiKey: process.env.NEXT_PUBLIC_GOOGLE_MAPS_API_KEY || '',
    libraries,
  });

  useEffect(() => {
    // Set Fort Wayne as the initial center when map loads
    if (map && !currentLocation && !selectedLocation) {
      const fortWayneLocation = new google.maps.LatLng(41.0793, -85.1394);
      map.panTo(fortWayneLocation);
      map.setZoom(13);
    }

    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(
        (position) => {
          const { latitude, longitude } = position.coords;
          const location = new google.maps.LatLng(latitude, longitude);
          setCurrentLocation(location);
          // Only pan to user location if they haven't selected anything yet
          if (map && !selectedLocation) {
            map.panTo(location);
          }
        },
        (error) => {
          console.error('Error getting location:', error);
          // If geolocation fails, ensure we stay centered on Fort Wayne
          if (map) {
            const fortWayneLocation = new google.maps.LatLng(41.0793, -85.1394);
            map.panTo(fortWayneLocation);
          }
        },
      );
    }
  }, [map, selectedLocation]);

  const onMapLoad = (map: google.maps.Map) => {
    setMap(map);
  };

  const onPlaceSelect = useCallback((place: google.maps.places.PlaceResult) => {
    if (place && place.geometry && place.geometry.location) {
      const location = new google.maps.LatLng(
        place.geometry.location.lat(),
        place.geometry.location.lng(),
      );
      setSelectedLocation(location);

      if (map) {
        map.panTo(location);
        map.setZoom(15);
      }

      if (onLocationSelect) {
        onLocationSelect({
          lat: location.lat(),
          lng: location.lng(),
          address: place.formatted_address || place.name || '',
        });
      }
    }
  }, [map, onLocationSelect]);

  const handleCurrentLocation = () => {
    if (currentLocation && map) {
      map.panTo(currentLocation);
      map.setZoom(15);
      setSelectedLocation(currentLocation);
      if (onLocationSelect) {
        onLocationSelect({
          lat: currentLocation.lat(),
          lng: currentLocation.lng(),
          address: 'Current Location',
        });
      }
    }
  };

  if (loadError) {
    return <div>Error loading maps</div>;
  }

  if (!isLoaded) {
    return <div>Loading maps...</div>;
  }

  return (
    <Card className="w-full overflow-hidden">
      {showSearchBar && (
        <div className="p-4 border-b">
          <div className="relative">
            <PlaceAutocomplete
              onPlaceSelect={onPlaceSelect}
              placeholder="Enter address in Fort Wayne, IN..."
              className="pl-10 pr-12 py-2 w-full"
            />
            <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400 h-5 w-5" />
            <Button
              type="button"
              variant="ghost"
              size="sm"
              className="absolute right-2 top-1/2 transform -translate-y-1/2"
              onClick={handleCurrentLocation}
              title="Use current location"
            >
              <Navigation className="h-4 w-4" />
            </Button>
          </div>
        </div>
      )}

      <div className="relative" style={{ height }}>
        <GoogleMap
          mapContainerStyle={{ width: '100%', height: '100%' }}
          center={defaultLocation}
          zoom={13}
          onLoad={onMapLoad}
          options={{
            styles: [
              {
                featureType: 'poi',
                elementType: 'labels',
                stylers: [{ visibility: 'off' }],
              },
            ],
            disableDefaultUI: true,
            zoomControl: true,
          }}
        >
          {selectedLocation && (
            <Marker
              position={selectedLocation}
              animation={google.maps.Animation.DROP}
            />
          )}
        </GoogleMap>

        {!showSearchBar && (
          <Button
            onClick={handleCurrentLocation}
            className="absolute bottom-4 right-4 bg-white text-black hover:bg-gray-100 shadow-lg"
            size="icon"
            title="Use current location"
          >
            <Navigation className="h-5 w-5" />
          </Button>
        )}
      </div>
    </Card>
  );
}

'use client';

import React, { useRef, useEffect } from 'react';
import { useJsApiLoader } from '@react-google-maps/api';
import { MapPin } from 'lucide-react';
import { Input } from '../ui/input';
import { cn } from '@/lib/utils';

const libraries: 'places'[] = ['places'];

interface PlaceDetails {
  formatted_address: string;
  place_id: string;
  geometry: {
    location: {
      lat: () => number;
      lng: () => number;
    };
  };
  address_components?: google.maps.GeocoderAddressComponent[];
  name?: string;
}

interface PlaceAutocompleteProps {
  onPlaceSelect: (place: PlaceDetails) => void;
  placeholder?: string;
  className?: string;
  disabled?: boolean;
  showIcon?: boolean;
  types?: string[];
  componentRestrictions?: { country?: string | string[] };
  bounds?: google.maps.LatLngBounds | google.maps.LatLngBoundsLiteral;
  strictBounds?: boolean;
  value?: string;
  onChange?: (value: string) => void;
}

export default function PlaceAutocomplete({
  onPlaceSelect,
  placeholder = 'Enter an address...',
  className = '',
  disabled = false,
  showIcon = true,
  types = ['address'],
  componentRestrictions,
  bounds,
  strictBounds = false,
  value,
  onChange,
}: PlaceAutocompleteProps) {
  const containerRef = useRef<HTMLDivElement>(null);
  const autocompleteElementRef = useRef<any>(null);

  // Use the Google Maps API loader hook
  const { isLoaded, loadError } = useJsApiLoader({
    id: 'google-map-script',
    googleMapsApiKey: process.env.NEXT_PUBLIC_GOOGLE_MAPS_API_KEY || '',
    libraries: libraries,
  });

  // Initialize PlaceAutocompleteElement when API is loaded
  useEffect(() => {
    if (!isLoaded || !containerRef.current || !window.google?.maps?.places?.PlaceAutocompleteElement) {
      return;
    }

    try {
      // Create the new PlaceAutocompleteElement
      const autocompleteElement = new window.google.maps.places.PlaceAutocompleteElement({
        types,
        componentRestrictions,
        bounds,
        strictBounds,
      });

      // Configure the element
      autocompleteElement.placeholder = placeholder;
      if (disabled) {
        autocompleteElement.setAttribute('disabled', 'true');
      }
      if (value) {
        autocompleteElement.value = value;
      }

      // Apply styling to match the project's Input component
      autocompleteElement.className = cn(
        'flex h-9 w-full rounded-md border border-input bg-transparent px-3 py-1 text-sm shadow-sm transition-colors file:border-0 file:bg-transparent file:text-sm file:font-medium placeholder:text-muted-foreground focus-visible:outline-none focus-visible:ring-1 focus-visible:ring-ring disabled:cursor-not-allowed disabled:opacity-50',
        showIcon ? 'pl-10' : '',
        className,
      );

      // Add event listeners
      const handlePlaceSelect = (event: any) => {
        const place = event.place;

        if (place && place.location) {
          const placeDetails: PlaceDetails = {
            formatted_address: place.formattedAddress || '',
            place_id: place.id || '',
            geometry: {
              location: {
                lat: () => place.location.lat(),
                lng: () => place.location.lng(),
              },
            },
            address_components: place.addressComponents || [],
            name: place.displayName,
          };

          onPlaceSelect(placeDetails);

          // Update external value if onChange is provided
          if (onChange) {
            onChange(place.formattedAddress || '');
          }
        }
      };

      const handleInput = (event: any) => {
        if (onChange) {
          onChange(event.target.value);
        }
      };

      autocompleteElement.addEventListener('gmp-placeselect', handlePlaceSelect);
      autocompleteElement.addEventListener('input', handleInput);

      // Add to container
      containerRef.current.appendChild(autocompleteElement);
      autocompleteElementRef.current = autocompleteElement;

      // Cleanup function
      return () => {
        if (autocompleteElement) {
          autocompleteElement.removeEventListener('gmp-placeselect', handlePlaceSelect);
          autocompleteElement.removeEventListener('input', handleInput);
          if (autocompleteElement.parentNode) {
            autocompleteElement.parentNode.removeChild(autocompleteElement);
          }
        }
      };
    } catch (error) {
      console.error('Error initializing PlaceAutocompleteElement:', error);
    }
  }, [isLoaded, onPlaceSelect, placeholder, disabled, types, componentRestrictions, bounds, strictBounds, className, showIcon, onChange, value]);

  // Update value when prop changes
  useEffect(() => {
    if (autocompleteElementRef.current && value !== undefined) {
      autocompleteElementRef.current.value = value;
    }
  }, [value]);

  // Update disabled state when prop changes
  useEffect(() => {
    if (autocompleteElementRef.current) {
      if (disabled) {
        autocompleteElementRef.current.setAttribute('disabled', 'true');
      } else {
        autocompleteElementRef.current.removeAttribute('disabled');
      }
    }
  }, [disabled]);

  if (loadError) {
    return (
      <div className={cn('relative', className)}>
        {showIcon && (
          <div className="absolute left-3 top-1/2 transform -translate-y-1/2 pointer-events-none z-10">
            <MapPin className="w-4 h-4 text-muted-foreground" />
          </div>
        )}
        <Input
          placeholder={`Error: ${loadError}`}
          disabled
          className={cn('h-9', showIcon ? 'pl-10' : '')}
        />
      </div>
    );
  }

  if (!isLoaded) {
    return (
      <div className={cn('relative', className)}>
        {showIcon && (
          <div className="absolute left-3 top-1/2 transform -translate-y-1/2 pointer-events-none z-10">
            <MapPin className="w-4 h-4 text-muted-foreground" />
          </div>
        )}
        <Input
          placeholder="Loading..."
          disabled
          className={cn('h-9', showIcon ? 'pl-10' : '')}
        />
      </div>
    );
  }

  return (
    <div className={cn('relative', className)}>
      {showIcon && (
        <div className="absolute left-3 top-1/2 transform -translate-y-1/2 pointer-events-none z-10">
          <MapPin className="w-4 h-4 text-muted-foreground" />
        </div>
      )}
      <div ref={containerRef} className="w-full" />
    </div>
  );
}

