        </div>
      </div>

      {showCustomizationModal && (
        <CustomizationModal
          item={item}
          customizationOptions={customizationOptions}
          onClose={() => setShowCustomizationModal(false)}
          onAddToCart={handleCustomizedAddToCart}
        />
      )}
    </>
  );
};

