// Dynamically import the map component to avoid SSR issues
const MapComponent = dynamic(() => import('@/components/maps/MapSearch'), {
  ssr: false,
  loading: () => (
    <div className="h-[400px] w-full bg-gray-100 animate-pulse"></div>
  ),
