'use client';

import React, { createContext, useContext, useEffect, useState } from 'react';
import {
  User as FirebaseUser,
  onAuthStateChanged,
  signInWithEmailAndPassword,
  createUserWithEmailAndPassword,
  signOut,
  updateProfile,
  sendPasswordResetEmail,
} from 'firebase/auth';
import { doc, setDoc, getDoc } from 'firebase/firestore';
import { auth, db } from '../lib/firebase-config';
import { useRouter } from 'next/navigation';

interface User {
  uid: string;
  email: string | null;
  displayName: string | null;
  photoURL: string | null;
}

interface AuthContextType {
  user: User | null;
  loading: boolean;
  signIn: (email: string, password: string) => Promise<void>;
  signUp: (email: string, password: string, displayName: string) => Promise<void>;
  logout: () => Promise<void>;
  resetPassword: (email: string) => Promise<void>;
}

const AuthContext = createContext<AuthContextType>({} as AuthContextType);

export function useAuth() {
  const context = useContext(AuthContext);
  if (!context) {
    throw new Error('useAuth must be used within an AuthProvider');
  }
  return context;
}

export function AuthProvider({ children }: { children: React.ReactNode }) {
  const [user, setUser] = useState<User | null>(null);
  const [loading, setLoading] = useState(true);
  const router = useRouter();

  useEffect(() => {
    const unsubscribe = onAuthStateChanged(auth, async (firebaseUser: FirebaseUser | null) => {
      if (firebaseUser) {
        const userData: User = {
          uid: firebaseUser.uid,
          email: firebaseUser.email,
          displayName: firebaseUser.displayName,
          photoURL: firebaseUser.photoURL,
        };
        setUser(userData);

        // Create or update user document in Firestore
        try {
          const userDocRef = doc(db, 'users', firebaseUser.uid);
          const userDoc = await getDoc(userDocRef);

          if (!userDoc.exists()) {
            await setDoc(userDocRef, {
              uid: firebaseUser.uid,
              email: firebaseUser.email,
              displayName: firebaseUser.displayName,
              photoURL: firebaseUser.photoURL,
              createdAt: new Date().toISOString(),
              updatedAt: new Date().toISOString(),
            });
          }
        } catch (error) {
          console.error('Error creating user document:', error);
        }
      } else {
        setUser(null);
      }
      setLoading(false);
    });

    return () => unsubscribe();
  }, []);

  const signIn = async (email: string, password: string) => {
    try {
      setLoading(true);
      await signInWithEmailAndPassword(auth, email, password);
      router.push('/dashboard');
    } catch (error: any) {
      console.error('Sign in error:', error);
      throw new Error(error.message || 'Failed to sign in');
    } finally {
      setLoading(false);
    }
  };

  const signUp = async (email: string, password: string, displayName: string) => {
    try {
      setLoading(true);
      const { user: firebaseUser } = await createUserWithEmailAndPassword(auth, email, password);

      // Update the user's display name
      await updateProfile(firebaseUser, {
        displayName: displayName,
      });

      router.push('/dashboard');
    } catch (error: any) {
      console.error('Sign up error:', error);
      throw new Error(error.message || 'Failed to create account');
    } finally {
      setLoading(false);
    }
  };

  const logout = async () => {
    try {
      await signOut(auth);
      router.push('/');
    } catch (error: any) {
      console.error('Logout error:', error);
      throw new Error(error.message || 'Failed to log out');
    }
  };

  const resetPassword = async (email: string) => {
    try {
      await sendPasswordResetEmail(auth, email);
    } catch (error: any) {
      console.error('Password reset error:', error);
      throw new Error(error.message || 'Failed to send password reset email');
    }
  };

  const value = {
    user,
    loading,
    signIn,
    signUp,
    logout,
    resetPassword,
  };

  return (
    <AuthContext.Provider value={value}>
      {children}
    </AuthContext.Provider>
  );
}

import { useState, useCallback } from 'react';
import {
  DocumenuRestaurant,
  ZomatoRestaurant,
  YelpBusiness,
  KrogerProduct,
  BestBuyProduct,
} from '@/lib/services/external-apis';

interface APIResponse<T> {
  success: boolean;
  data?: T;
  error?: string;
}

interface UnifiedRestaurantResults {
  documenu: DocumenuRestaurant[];
  zomato: ZomatoRestaurant[];
  yelp: YelpBusiness[];
  errors: string[];
}

interface UnifiedProductResults {
  kroger: KrogerProduct[];
  bestbuy: BestBuyProduct[];
  errors: string[];
}

export function useExternalAPIs() {
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);

  const makeAPICall = useCallback(async <T>(url: string): Promise<T | null> => {
    try {
      setLoading(true);
      setError(null);

      const response = await fetch(url);
      const result: APIResponse<T> = await response.json();

      if (!result.success) {
        throw new Error(result.error || 'API call failed');
      }

      return result.data || null;
    } catch (err) {
      const errorMessage = err instanceof Error ? err.message : 'Unknown error occurred';
      setError(errorMessage);
      console.error('API call error:', err);
      return null;
    } finally {
      setLoading(false);
    }
  }, []);

  // Documenu API calls
  const searchDocumenuRestaurants = useCallback(async (params: {
    lat?: number;
    lon?: number;
    distance?: number;
    size?: number;
    page?: number;
    fullmenu?: boolean;
    key_phrase?: string;
    exact_match?: boolean;
  }) => {
    const searchParams = new URLSearchParams({
      action: 'search',
      ...Object.fromEntries(
        Object.entries(params)
          .filter(([, v]) => v !== undefined)
          .map(([k, v]) => [k, String(v)]),
      ),
    });

    return makeAPICall<DocumenuRestaurant[]>(`/api/external/documenu?${searchParams}`);
  }, [makeAPICall]);

  const getDocumenuMenu = useCallback(async (restaurantId: string) => {
    const searchParams = new URLSearchParams({
      action: 'menu',
      restaurantId,
    });

    return makeAPICall<DocumenuRestaurant>(`/api/external/documenu?${searchParams}`);
  }, [makeAPICall]);

  // Zomato API calls
  const searchZomatoRestaurants = useCallback(async (params: {
    entity_id?: number;
    entity_type?: string;
    q?: string;
    start?: number;
    count?: number;
    lat?: number;
    lon?: number;
    radius?: number;
    cuisines?: string;
    establishment_type?: string;
    collection_id?: number;
    category?: string;
    sort?: string;
    order?: string;
  }) => {
    const searchParams = new URLSearchParams({
      action: 'search',
      ...Object.fromEntries(
        Object.entries(params)
          .filter(([, v]) => v !== undefined)
          .map(([k, v]) => [k, String(v)]),
      ),
    });

    return makeAPICall<ZomatoRestaurant[]>(`/api/external/zomato?${searchParams}`);
  }, [makeAPICall]);

  const getZomatoRestaurantDetails = useCallback(async (restaurantId: string) => {
    const searchParams = new URLSearchParams({
      action: 'details',
      restaurantId,
    });

    return makeAPICall<ZomatoRestaurant>(`/api/external/zomato?${searchParams}`);
  }, [makeAPICall]);

  // Yelp API calls
  const searchYelpBusinesses = useCallback(async (params: {
    term?: string;
    location?: string;
    latitude?: number;
    longitude?: number;
    radius?: number;
    categories?: string;
    locale?: string;
    limit?: number;
    offset?: number;
    sort_by?: 'best_match' | 'rating' | 'review_count' | 'distance';
    price?: '1' | '2' | '3' | '4';
    open_now?: boolean;
    open_at?: number;
    attributes?: string;
  }) => {
    const searchParams = new URLSearchParams({
      action: 'search',
      ...Object.fromEntries(
        Object.entries(params)
          .filter(([, v]) => v !== undefined)
          .map(([k, v]) => [k, String(v)]),
      ),
    });

    return makeAPICall<{ businesses: YelpBusiness[]; total: number; region: any }>(
      `/api/external/yelp?${searchParams}`,
    );
  }, [makeAPICall]);

  const getYelpBusinessDetails = useCallback(async (businessId: string) => {
    const searchParams = new URLSearchParams({
      action: 'details',
      businessId,
    });

    return makeAPICall<YelpBusiness>(`/api/external/yelp?${searchParams}`);
  }, [makeAPICall]);

  const getYelpBusinessReviews = useCallback(async (businessId: string, locale?: string) => {
    const searchParams = new URLSearchParams({
      action: 'reviews',
      businessId,
      ...(locale && { locale }),
    });

    return makeAPICall<{
      reviews: Array<{
        id: string;
        url: string;
        text: string;
        rating: number;
        time_created: string;
        user: {
          id: string;
          profile_url: string;
          image_url: string;
          name: string;
        };
      }>;
      total: number;
      possible_languages: string[];
    }>(`/api/external/yelp?${searchParams}`);
  }, [makeAPICall]);

  // Kroger API calls
  const searchKrogerProducts = useCallback(async (params: {
    q?: string;
    locationId?: string;
    productId?: string;
    brand?: string;
    fulfillment?: 'ais' | 'csp' | 'dug' | 'sto';
    start?: number;
    limit?: number;
