    } catch (error) {
      console.error('Failed to save log entry:', error);
    }
  }

  public async logRequest(
    req: NextRequest,
    res: NextResponse,
    userId: string | undefined,
    startTime: number,
    error?: Error,
  ): Promise<void> {
    const endTime = Date.now();
    const duration = endTime - startTime;

    let requestBody;
    if (req.body) {
      try {
        const clone = req.clone();
        requestBody = await clone.json();
      } catch {
        requestBody = undefined;
      }
    }

    let responseBody;
    try {
      responseBody = await res.clone().json();
    } catch {
      responseBody = undefined;
    }

    const url = new URL(req.url);
    const entry: LogEntry = {
      timestamp: new Date(),
      method: req.method,
      path: url.pathname,
      query: Object.fromEntries(url.searchParams),
      userId,
      ip: req.ip,
      userAgent: req.headers.get('user-agent') || undefined,
      requestBody: this.truncateBody(this.sanitizeBody(requestBody)),
      responseStatus: res.status,
      responseBody: this.truncateBody(this.sanitizeBody(responseBody)),
      duration,
      error: error
        ? {
            message: error.message,
            code: (error as any).code,
            stack:
              process.env.NODE_ENV === 'development' ? error.stack : undefined,
          }
        : undefined,
    };

    await this.saveLog(entry);
  }

